import { useEffect, useState } from "react";
import { getApplicants } from "../../services/api";

const ApplicantsList = () => {
  const [applicants, setApplicants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isListVisible, setIsListVisible] = useState(false);

  useEffect(() => {
    const fetchApplicants = async () => {
      try {
        const employerId = localStorage.getItem("employerId");
        if (!employerId) {
          console.error("No employerId in localStorage. Make sure you save it on login/signup.");
          setLoading(false);
          return;
        }

        const response = await getApplicants(employerId);

        // ✅ Handles both {data: [...]} or direct [...]
        const data = response?.data?.applicants || response?.data || [];

        setApplicants(data);
      } catch (err) {
        console.error("Error fetching applicants:", err);
      } finally {
        setLoading(false);
        setTimeout(() => setIsListVisible(true), 100);
      }
    };

    fetchApplicants();
  }, []);

  if (loading) {
    return (
      <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-400 font-sans flex items-center justify-center">
        <p>Loading applicants...</p>
      </div>
    );
  }

  return (
    <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-100 font-sans">
      <h1 className="text-3xl font-bold text-white mb-8">All Applicants</h1>

      {!applicants.length ? (
        <div className="text-center p-20 text-gray-400">
          <p>You have no applicants yet.</p>
        </div>
      ) : (
        <div
          className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 transition-all duration-500 ease-in-out ${
            isListVisible ? "opacity-100" : "opacity-0"
          }`}
        >
          {applicants.map((applicant, index) => (
            <div
              key={applicant._id}
              className="bg-gray-800 p-6 rounded-2xl shadow-lg transition-all duration-500 ease-in-out transform hover:-translate-y-2 hover:shadow-2xl"
              style={{
                transitionDelay: `${index * 50}ms`,
                opacity: isListVisible ? 1 : 0,
                transform: isListVisible ? "translateY(0)" : "translateY(1rem)",
              }}
            >
              {/* Candidate Info */}
              <h3 className="text-xl font-semibold text-white truncate">
                {applicant.candidate?.name || "Unknown"}
              </h3>
              <p className="text-sm text-gray-400 mt-1">
                {applicant.candidate?.email || "N/A"}
              </p>

              {/* Resume (if available) */}
              {applicant.candidate?.resume && (
                <a
                  href={applicant.candidate.resume}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="mt-3 inline-block text-sm text-purple-400 hover:underline"
                >
                  View Resume
                </a>
              )}

              {/* Job + Status */}
              <div className="mt-4 pt-4 border-t border-gray-700">
                <p className="text-gray-300">
                  <span className="text-sm font-semibold">Applied for:</span>{" "}
                  <span className="text-purple-400">
                    {applicant.job?.title || "No job title"}
                  </span>
                </p>
                <div className="mt-2">
                  <span className="text-sm font-semibold text-gray-300">
                    Status:{" "}
                  </span>
                  <span
                    className={`inline-block px-3 py-1 text-xs font-bold rounded-full ${
                      applicant.status === "hired"
                        ? "bg-green-600 text-white"
                        : applicant.status === "rejected"
                        ? "bg-red-600 text-white"
                        : "bg-blue-600 text-white"
                    }`}
                  >
                    {applicant.status}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ApplicantsList;
