// job-portal-frontend/src/pages/Auth/SignupEmployer.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { User, Mail, Lock, Building, Landmark, Globe, MapPin } from 'lucide-react';
import '../../styles/Auth.css';
import { useNavigate } from "react-router-dom";

function EmployerSignup() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    companyName: '',
    companyId: '',
    companyWebsite: '',
    companyAddress: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const res = await axios.post('http://localhost:5000/api/employers/register', formData);

      // save token + role from backend
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("role", (res.data.role || "employer").toLowerCase());
      localStorage.setItem("user", JSON.stringify(res.data.employer || {}));

      setMessage(res.data.message || 'Signup successful!');
      navigate('/employer-dashboard'); // go straight to dashboard
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error signing up. Please try again.');
    }
  };


  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4 background-gradient font-inter">
      {/* Signup Card container */}
      <div className="w-full max-w-2xl bg-white/2 backdrop-blur-2xl rounded-3xl p-8 md:p-12 shadow-[0_8px_32px_rgba(0,0,0,0.37)] border border-white/20 glowing-border">
        <h2 className="text-4xl md:text-5xl font-extrabold text-black text-center mb-6 drop-shadow-lg">
          Employer Signup
        </h2>
        <p className="text-center text-black-300 mb-8 max-w-md mx-auto">
          Create your account to start posting jobs and finding top talent.
        </p>

        {/* Display message from submission */}
        {message && (
          <p className={`p-4 mb-6 rounded-xl text-center font-semibold text-lg drop-shadow-md ${message.includes('Error') ? 'bg-red-600/80 text-white' : 'bg-green-600/80 text-white'}`}>
            {message}
          </p>
        )}
        
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Full Name */}
          <div className="relative">
            <label className="block text-black-200 text-sm font-medium mb-1">Full Name</label>
            <div className="relative">
              <User className="absolute left-4 top-1/2 -translate-y-1/2 text-black-400 z-10" size={20} />
              <input
                type="text"
                name="name"
                placeholder="John Doe"
                value={formData.name}
                onChange={handleChange}
                required
                className="w-full pl-12 pr-4 py-3 bg-white/2 text-black placeholder-black-400 rounded-xl border border-white/10 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 shadow-inner"
              />
            </div>
          </div>
          {/* Email */}
          <div className="relative">
            <label className="block text-black-200 text-sm font-medium mb-1">Email</label>
            <div className="relative">
              <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-black-400 z-10" size={20} />
              <input
                type="email"
                name="email"
                placeholder="you@company.com"
                value={formData.email}
                onChange={handleChange}
                required
                className="w-full pl-12 pr-4 py-3 bg-white/2 text-black placeholder-black-400 rounded-xl border border-white/10 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 shadow-inner"
              />
            </div>
          </div>
          {/* Password */}
          <div className="relative col-span-1 md:col-span-2">
            <label className="block text-black-200 text-sm font-medium mb-1">Password</label>
            <div className="relative">
              <Lock className="absolute left-4 top-1/2 -translate-y-1/2 text-black-400 z-10" size={20} />
              <input
                type="password"
                name="password"
                placeholder="••••••••"
                value={formData.password}
                onChange={handleChange}
                required
                className="w-full pl-12 pr-4 py-3 bg-white/2 text-black placeholder-black-400 rounded-xl border border-white/10 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 shadow-inner"
              />
            </div>
          </div>
          {/* Company Name */}
          <div className="relative">
            <label className="block text-black-200 text-sm font-medium mb-1">Company Name</label>
            <div className="relative">
              <Building className="absolute left-4 top-1/2 -translate-y-1/2 text-black-400 z-10" size={20} />
              <input
                type="text"
                name="companyName"
                placeholder="Acme Inc."
                value={formData.companyName}
                onChange={handleChange}
                required
                className="w-full pl-12 pr-4 py-3 bg-white/2 text-black placeholder-black-400 rounded-xl border border-white/10 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 shadow-inner"
              />
            </div>
          </div>
          {/* Company ID */}
          <div className="relative">
            <label className="block text-black-200 text-sm font-medium mb-1">Company ID</label>
            <div className="relative">
              <Landmark className="absolute left-4 top-1/2 -translate-y-1/2 text-black-400 z-10" size={20} />
              <input
                type="text"
                name="companyId"
                placeholder="123456789"
                value={formData.companyId}
                onChange={handleChange}
                required
                className="w-full pl-12 pr-4 py-3 bg-white/2 text-black placeholder-black-400 rounded-xl border border-white/10 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 shadow-inner"
              />
            </div>
          </div>
          {/* Company Website */}
          <div className="relative">
            <label className="block text-black-200 text-sm font-medium mb-1">Company Website (optional)</label>
            <div className="relative">
              <Globe className="absolute left-4 top-1/2 -translate-y-1/2 text-black-400 z-10" size={20} />
              <input
                type="text"
                name="companyWebsite"
                placeholder="https://www.acme.com"
                value={formData.companyWebsite}
                onChange={handleChange}
                className="w-full pl-12 pr-4 py-3 bg-white/2 text-black placeholder-black-400 rounded-xl border border-white/10 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 shadow-inner"
              />
            </div>
          </div>
          {/* Company Address */}
          <div className="relative">
            <label className="block text-black-200 text-sm font-medium mb-1">Company Address (optional)</label>
            <div className="relative">
              <MapPin className="absolute left-4 top-1/2 -translate-y-1/2 text-black-400 z-10" size={20} />
              <input
                type="text"
                name="companyAddress"
                placeholder="123 Corporate Ave"
                value={formData.companyAddress}
                onChange={handleChange}
                className="w-full pl-12 pr-4 py-3 bg-white/2 text-black placeholder-black-400 rounded-xl border border-white/10 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 shadow-inner"
              />
            </div>
          </div>
          
          {/* Submit Button */}
          <div className="col-span-1 md:col-span-2 mt-4">
            <button
              type="submit"
              className="w-full p-4 font-bold rounded-xl text-lg text-white bg-gradient-to-r from-[#8e2de2] to-[#4a00e0] hover:from-[#4a00e0] hover:to-[#8e2de2] transition-all duration-300 shadow-xl transform hover:scale-105"
            >
              Sign Up
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EmployerSignup;
