// src/pages/JobDetail.js
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios'; // Assuming you have axios installed for API calls

export default function JobDetails() {
  const { id } = useParams();
  const [job, setJob] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    coverLetter: '',
    resume: null,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await fetch(`/api/jobs/${id}`);
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        const data = await res.json();
        setJob(data);
      } catch (err) {
        console.error("Failed to fetch job", err);
        setError("Failed to load job details. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
    fetchJob();
  }, [id]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'resume') {
      setFormData({ ...formData, resume: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('jobId', job._id);
    data.append('name', formData.name);
    data.append('email', formData.email);
    data.append('coverLetter', formData.coverLetter);
    data.append('resume', formData.resume);

    try {
      await axios.post('http://localhost:5000/api/apply', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Your application has been submitted successfully!');
      setShowForm(false);
      // Optionally reset form
      setFormData({
        name: '',
        email: '',
        coverLetter: '',
        resume: null,
      });
    } catch (error) {
      console.error('Error submitting application:', error);
      alert('There was an error submitting your application. Please try again.');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-50 to-indigo-100">
        <div className="text-xl font-semibold text-gray-700 animate-pulse">Loading amazing job details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-red-50">
        <div className="text-xl text-red-700">{error}</div>
      </div>
    );
  }

  if (!job) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gray-50">
        <div className="text-xl text-gray-500">Job not found.</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6 md:p-10 flex items-center justify-center">
      <div className="max-w-4xl w-full bg-white rounded-xl shadow-2xl overflow-hidden transform transition-all duration-300 hover:scale-[1.01] border border-blue-200">
        <div className="p-8">
          <h2 className="text-4xl font-extrabold text-blue-900 mb-4 tracking-tight leading-tight">{job.title}</h2>
          <p className="text-gray-700 font-semibold text-lg mb-2">
            <span className="text-blue-600">Company:</span> {job.company}
          </p>
          <p className="text-gray-600 text-lg mb-2">
            <span className="text-indigo-500">Location:</span> {job.location}
          </p>
          <p className="text-gray-600 text-lg mb-2">
            <span className="text-green-600">Salary:</span> {job.salary}
          </p>
          <p className="text-gray-600 text-lg mb-4">
            <span className="text-purple-600">Type:</span> {job.type}
          </p>

          <div className="bg-gray-50 p-6 rounded-lg border border-gray-200 mb-6">
            <h3 className="text-2xl font-bold text-gray-800 mb-3">Job Description</h3>
            <p className="text-gray-800 leading-relaxed text-base whitespace-pre-line">{job.description}</p>
          </div>

          {job.requirements?.length > 0 && (
            <div className="mt-6 bg-blue-50 p-6 rounded-lg border border-blue-200">
              <h3 className="text-2xl text-blue-800 font-bold mb-3">Key Requirements:</h3>
              <ul className="list-disc list-inside space-y-2 text-gray-700 text-base">
                {job.requirements.map((req, index) => (
                  <li key={index} className="flex items-start">
                    <span className="text-blue-500 mr-2">&#8226;</span>
                    <span>{req}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}

          <div className="mt-8 pt-6 border-t border-gray-200">
            <button
              onClick={() => setShowForm(!showForm)}
              className="w-full md:w-auto bg-gradient-to-r from-blue-600 to-indigo-700 hover:from-blue-700 hover:to-indigo-800 text-white font-bold py-3 px-8 rounded-full shadow-lg transform transition-all duration-300 hover:scale-105 focus:outline-none focus:ring-4 focus:ring-blue-300"
            >
              {showForm ? 'Hide Application Form' : 'Apply Now!'}
            </button>

            {showForm && (
              <form onSubmit={handleSubmit} className="mt-8 p-6 bg-white border border-gray-200 rounded-lg shadow-inner animate-fade-in-down">
                <h3 className="text-2xl font-bold text-gray-800 mb-5">Submit Your Application</h3>
                <div className="space-y-5">
                  <div>
                    <label htmlFor="name" className="block text-gray-700 text-sm font-medium mb-1">Your Full Name</label>
                    <input
                      type="text"
                      name="name"
                      id="name"
                      placeholder="John Doe"
                      required
                      onChange={handleChange}
                      value={formData.name}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200 ease-in-out text-gray-800"
                    />
                  </div>
                  <div>
                    <label htmlFor="email" className="block text-gray-700 text-sm font-medium mb-1">Your Email Address</label>
                    <input
                      type="email"
                      name="email"
                      id="email"
                      placeholder="john.doe@example.com"
                      required
                      onChange={handleChange}
                      value={formData.email}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200 ease-in-out text-gray-800"
                    />
                  </div>
                  <div>
                    <label htmlFor="coverLetter" className="block text-gray-700 text-sm font-medium mb-1">Cover Letter (Optional)</label>
                    <textarea
                      name="coverLetter"
                      id="coverLetter"
                      placeholder="Tell us why you're a great fit for this role..."
                      onChange={handleChange}
                      value={formData.coverLetter}
                      rows="5"
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200 ease-in-out text-gray-800"
                    />
                  </div>
                  <div>
                    <label htmlFor="resume" className="block text-gray-700 text-sm font-medium mb-1">Upload Your Resume (PDF, DOC, DOCX)</label>
                    <input
                      type="file"
                      name="resume"
                      id="resume"
                      accept=".pdf,.doc,.docx"
                      required
                      onChange={handleChange}
                      className="w-full text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100 cursor-pointer"
                    />
                  </div>
                  <button
                    type="submit"
                    className="w-full bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white font-bold py-3 px-8 rounded-lg shadow-md transform transition-all duration-300 hover:scale-105 focus:outline-none focus:ring-4 focus:ring-green-300"
                  >
                    Submit Application
                  </button>
                </div>
              </form>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
