// src/pages/Auth/LoginCandidate.jsx
// src/pages/Auth/LoginCandidate.jsx
import React, { useState, useContext } from "react";
import API from "../../services/api";
import { useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import '../../styles/Auth.css';

const LoginCandidate = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post("/auth/login", formData);

      // ✅ Update AuthContext
      login(res.data.user, res.data.token);

      // ✅ Candidate goes to home, then navbar shows Candidate Dashboard
      navigate("/");
    } catch (err) {
      console.error("Login failed:", err);
      alert("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="container">
      <div className="login-card-container">
        <div className="login-left-panel">
          <div className="logo">Your Logo</div>
          <h2 className="welcome-heading">Hello, welcome!</h2>
          <p className="welcome-text">
            Find your dream job with us. Lorem ipsum dolor sit amet, consectetur adipiscing elit.
          </p>
          <button className="view-more-btn">View more</button>
        </div>

        <div className="login-right-panel">
          <h2 className="form-heading">Candidate Login</h2>
          <form onSubmit={handleSubmit} className="login-form">
            <input
              type="email"
              name="email"
              placeholder="Email address"
              onChange={handleChange}
              required
              className="form-input"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              onChange={handleChange}
              required
              className="form-input"
            />
            <div className="form-extras">
              <label className="remember-me">
                <input type="checkbox" /> Remember me
              </label>
              <Link to="/forgot-password" className="forgot-password-link">
                Forgot password?
              </Link>
            </div>
            <div className="form-actions">
              <button type="submit" className="login-btn">Login</button>
              <Link to="/signup-candidate" className="signup-btn">Sign up</Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginCandidate;
