import React, { useEffect, useState } from "react";
import axios from "axios";

const CompanyProfile = () => {
  const [profile, setProfile] = useState({});
  const [loading, setLoading] = useState(true);
  const [success, setSuccess] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/employers/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setProfile(res.data);
      } catch (err) {
        console.error("Error fetching profile:", err);
      } finally {
        setLoading(false);
        // Animate the form after data is loaded
        setTimeout(() => setIsFormVisible(true), 100);
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => setProfile({ ...profile, [e.target.name]: e.target.value });

  const handleSave = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.put("http://localhost:5000/api/employers/profile", profile, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess(true);
      setTimeout(() => setSuccess(false), 3000); // Hide success message after 3 seconds
    } catch (err) {
      console.error("Error updating profile:", err);
      alert("Failed to update profile."); // Fallback to original alert for errors
    }
  };

  if (loading) {
    return (
      <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-400 font-sans flex items-center justify-center">
        <p>Loading profile...</p>
      </div>
    );
  }

  return (
    <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-100 font-sans">
      <h1 className="text-3xl font-bold text-white mb-8">Company Profile</h1>

      <div
        className={`bg-gray-800 p-8 rounded-2xl shadow-xl transition-all duration-500 ease-in-out transform ${
          isFormVisible ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
        }`}
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <label className="block">
            <span className="text-gray-400 text-sm">Company Name</span>
            <input
              name="name"
              value={profile.name || ""}
              onChange={handleChange}
              placeholder="Company Name"
              className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200"
            />
          </label>
          <label className="block">
            <span className="text-gray-400 text-sm">Logo URL</span>
            <input
              name="logo"
              value={profile.logo || ""}
              onChange={handleChange}
              placeholder="Logo URL"
              className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200"
            />
          </label>
          <label className="block">
            <span className="text-gray-400 text-sm">Location</span>
            <input
              name="location"
              value={profile.location || ""}
              onChange={handleChange}
              placeholder="Location"
              className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200"
            />
          </label>
          <label className="block">
            <span className="text-gray-400 text-sm">Website</span>
            <input
              name="website"
              value={profile.website || ""}
              onChange={handleChange}
              placeholder="Website"
              className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200"
            />
          </label>
        </div>

        <label className="block mb-6">
          <span className="text-gray-400 text-sm">Description</span>
          <textarea
            name="description"
            value={profile.description || ""}
            onChange={handleChange}
            placeholder="Description"
            rows="5"
            className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200"
          ></textarea>
        </label>
        
        <button
          onClick={handleSave}
          className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-purple-500"
        >
          Save Profile
        </button>

        {success && (
          <div className="mt-4 text-green-400 text-sm font-medium">
            Profile updated successfully!
          </div>
        )}
      </div>
    </div>
  );
};

export default CompanyProfile;
