import { useEffect, useState } from "react";
import axios from "axios";
import { getEmployerStats } from "../../services/api";

const Stats = () => {
  const [stats, setStats] = useState(null);
  const [isStatsVisible, setIsStatsVisible] = useState(false);

  useEffect(() => {
    // Keep the logic same for fetching data
    const fetchStats = async () => {
      try {
        const { data } = await getEmployerStats();
        setStats(data);
      } catch (err) {
        console.error("Error fetching stats:", err);
      }
    };
    fetchStats();

    // Staggered animation
    const statsTimer = setTimeout(() => {
      setIsStatsVisible(true);
    }, 200);

    return () => clearTimeout(statsTimer);
  }, []);

  return (
    <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-100 font-sans">
      <h1 className="text-3xl font-bold text-white mb-8">Employer Stats</h1>

      {stats ? (
        <div
          className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 transition-all duration-500 ease-in-out transform ${
            isStatsVisible ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
          }`}
        >
          {/* Total Jobs Card */}
          <div className="bg-gray-800 p-6 rounded-2xl shadow-lg transition-transform duration-300 hover:-translate-y-2 transform hover:shadow-2xl">
            <h4 className="text-sm font-medium text-gray-400">Total Jobs</h4>
            <p className="text-4xl font-bold mt-2 text-white">{stats.totalJobs}</p>
          </div>

          {/* Total Applicants Card */}
          <div className="bg-gray-800 p-6 rounded-2xl shadow-lg transition-transform duration-300 hover:-translate-y-2 transform hover:shadow-2xl">
            <h4 className="text-sm font-medium text-gray-400">Total Applicants</h4>
            <p className="text-4xl font-bold mt-2 text-white">{stats.totalApplicants}</p>
          </div>

          {/* Transparent Card for Most Popular Job */}
          <div className="bg-white bg-opacity-5 p-6 rounded-2xl shadow-lg backdrop-blur-md transition-transform duration-300 hover:-translate-y-2 transform hover:shadow-2xl border border-gray-700">
            <h4 className="text-sm font-medium text-gray-300">Most Popular Job</h4>
            <p className="text-4xl font-bold mt-2 text-white truncate">{stats.mostPopularJob}</p>
          </div>
        </div>
      ) : (
        <div className="text-center p-20 text-gray-400">
          <p>Loading stats...</p>
        </div>
      )}
    </div>
  );
};

export default Stats;
