// src/pages/Dashboard/Candidate/Profile.js
// src/pages/Dashboard/Candidate/Profile.js
import React, { useState, useEffect } from "react";
import styled, { createGlobalStyle } from "styled-components";
import { motion, AnimatePresence } from "framer-motion";

// Global styles for the dark theme
const GlobalStyle = createGlobalStyle`
  body {
    background-color: #121212;
    color: #e0e0e0;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
`;

// Styled Components for the UI
const ProfileContainer = styled.div`
  max-width: 800px;
  margin: 40px auto;
  padding: 30px;
  background-color: #1f1f1f;
  border-radius: 12px;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.5);
`;

const Section = styled.div`
  margin-bottom: 30px;
  padding: 20px;
  border: 1px solid #333;
  border-radius: 10px;
  background-color: #2a2a2a;
`;

const H2 = styled.h2`
  color: #bb86fc;
  text-align: center;
  margin-bottom: 20px;
  font-size: 2.2rem;
`;

const H3 = styled.h3`
  color: #bb86fc;
  margin-bottom: 15px;
  font-size: 1.5rem;
  border-bottom: 2px solid #373737;
  padding-bottom: 5px;
`;

const InputGroup = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 15px;
`;

const Label = styled.label`
  margin-bottom: 8px;
  font-weight: bold;
  color: #b0b0b0;
`;

const Input = styled.input`
  padding: 12px;
  border: 1px solid #444;
  border-radius: 8px;
  background-color: #333;
  color: #e0e0e0;
`;

const Select = styled.select`
  padding: 12px;
  border: 1px solid #444;
  border-radius: 8px;
  background-color: #333;
  color: #e0e0e0;
`;

const Button = styled.button`
  padding: 12px 20px;
  background-color: #bb86fc;
  color: #121212;
  border: none;
  border-radius: 8px;
  font-weight: bold;
  cursor: pointer;
  margin-top: 15px;
  transition: all 0.3s ease;
  &:hover {
    background-color: #9c6cde;
  }
`;

const EducationItem = styled(motion.div)`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 15px;
`;

const TagContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 10px;
`;

const SkillTag = styled.span`
  background-color: #bb86fc;
  color: #121212;
  padding: 8px 12px;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: bold;
  cursor: pointer;
`;

const FileInputLabel = styled.label`
  display: inline-block;
  padding: 12px 20px;
  background-color: #bb86fc;
  color: #121212;
  border-radius: 8px;
  font-weight: bold;
  cursor: pointer;
`;

const FileInput = styled.input`
  display: none;
`;

const UploadedFileName = styled.p`
  margin-top: 10px;
  font-style: italic;
  color: #888;
`;

const SavedMessage = styled.p`
  margin-top: 15px;
  color: #4caf50;
  font-weight: bold;
`;

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "",
    email: "",
    phone: "",
    skills: [],
    education: [{ degree: "", institution: "", year: "" }],
    resume: null,
  });

  const [newSkill, setNewSkill] = useState("");
  const [saved, setSaved] = useState(false);

  const skillOptions = ["React", "Node.js", "Python", "Java", "SQL", "DevOps"];

  // Load saved profile on first render
  useEffect(() => {
    const savedProfile = localStorage.getItem("candidateProfile");
    if (savedProfile) {
      setProfile(JSON.parse(savedProfile));
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
    setSaved(false);
  };

  const handleSkillSelect = (e) => {
    const value = e.target.value;
    if (value && !profile.skills.includes(value)) {
      setProfile({ ...profile, skills: [...profile.skills, value] });
      setSaved(false);
    }
  };

  const handleAddCustomSkill = () => {
    if (newSkill && !profile.skills.includes(newSkill)) {
      setProfile({ ...profile, skills: [...profile.skills, newSkill] });
      setNewSkill("");
      setSaved(false);
    }
  };

  const handleRemoveSkill = (skillToRemove) => {
    setProfile({
      ...profile,
      skills: profile.skills.filter((skill) => skill !== skillToRemove),
    });
    setSaved(false);
  };

  const handleEducationChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEdu = [...profile.education];
    updatedEdu[index][name] = value;
    setProfile({ ...profile, education: updatedEdu });
    setSaved(false);
  };

  const addEducation = () => {
    setProfile({
      ...profile,
      education: [...profile.education, { degree: "", institution: "", year: "" }],
    });
    setSaved(false);
  };

  const handleResumeUpload = (e) => {
    setProfile({ ...profile, resume: e.target.files[0] });
    setSaved(false);
  };

  const handleSave = () => {
    localStorage.setItem("candidateProfile", JSON.stringify(profile));
    setSaved(true);
  };

  return (
    <>
      <GlobalStyle />
      <ProfileContainer as={motion.div} initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
        <H2>Candidate Profile</H2>

        {/* Basic Info */}
        <Section>
          <H3>Basic Information</H3>
          <InputGroup>
            <Label>Name</Label>
            <Input type="text" name="name" value={profile.name} onChange={handleChange} />
          </InputGroup>
          <InputGroup>
            <Label>Email</Label>
            <Input type="email" name="email" value={profile.email} onChange={handleChange} />
          </InputGroup>
          <InputGroup>
            <Label>Phone</Label>
            <Input type="text" name="phone" value={profile.phone} onChange={handleChange} />
          </InputGroup>
        </Section>

        {/* Skills */}
        <Section>
          <H3>Skills</H3>
          <InputGroup style={{ flexDirection: "row", gap: "10px", alignItems: "center" }}>
            <Select onChange={handleSkillSelect}>
              <option value="">Select a skill</option>
              {skillOptions.map((skill, index) => (
                <option key={index} value={skill}>
                  {skill}
                </option>
              ))}
            </Select>
            <Input
              type="text"
              placeholder="Add custom skill"
              value={newSkill}
              onChange={(e) => setNewSkill(e.target.value)}
              style={{ flexGrow: 1 }}
            />
            <Button onClick={handleAddCustomSkill}>Add Skill</Button>
          </InputGroup>
          <TagContainer>
            <AnimatePresence>
              {profile.skills.map((skill) => (
                <motion.div key={skill} initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                  <SkillTag onClick={() => handleRemoveSkill(skill)}>{skill}</SkillTag>
                </motion.div>
              ))}
            </AnimatePresence>
          </TagContainer>
        </Section>

        {/* Education */}
        <Section>
          <H3>Education</H3>
          <AnimatePresence>
            {profile.education.map((edu, index) => (
              <EducationItem key={index}>
                <Input type="text" name="degree" placeholder="Degree" value={edu.degree} onChange={(e) => handleEducationChange(index, e)} />
                <Input type="text" name="institution" placeholder="Institution" value={edu.institution} onChange={(e) => handleEducationChange(index, e)} />
                <Input type="text" name="year" placeholder="Year" value={edu.year} onChange={(e) => handleEducationChange(index, e)} />
              </EducationItem>
            ))}
          </AnimatePresence>
          <Button onClick={addEducation}>+ Add Education</Button>
        </Section>

        {/* Resume */}
        <Section>
          <H3>Resume</H3>
          <InputGroup>
            <FileInputLabel htmlFor="resume-upload">Upload Resume</FileInputLabel>
            <FileInput id="resume-upload" type="file" accept=".pdf,.doc,.docx" onChange={handleResumeUpload} />
            {profile.resume && <UploadedFileName><strong>Uploaded:</strong> {profile.resume.name}</UploadedFileName>}
          </InputGroup>
        </Section>

        {/* Save Button */}
        <Button onClick={handleSave}>Save Profile</Button>
        {saved && <SavedMessage>âœ… Profile saved successfully!</SavedMessage>}
      </ProfileContainer>
    </>
  );
};

export default Profile;
