import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Briefcase, MapPin, Search, DollarSign } from 'lucide-react';
import axios from 'axios';
import { motion } from 'framer-motion';
import Navbar from '../components/Navbar'; // Assuming you have a Navbar component

// Re-usable animation variants
const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
};

const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
        opacity: 1,
        y: 0,
        transition: {
            duration: 0.5,
            ease: "easeOut"
        }
    }
};

export default function BrowseJobs() {
    const [jobs, setJobs] = useState([]);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [locationFilter, setLocationFilter] = useState('');

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                // Mock data for demonstration since the API endpoint isn't provided
                const mockJobs = [
                    { _id: '1', title: 'Frontend Developer', company: 'Vercel', location: 'Remote', type: 'Full-time', salary: '120,000' },
                    { _id: '2', title: 'Backend Engineer', company: 'Stripe', location: 'San Francisco', type: 'Full-time', salary: '150,000' },
                    { _id: '3', title: 'UX/UI Designer', company: 'Figma', location: 'New York', type: 'Contract', salary: '95,000' },
                    { _id: '4', title: 'DevOps Specialist', company: 'GitHub', location: 'Remote', type: 'Full-time', salary: '135,000' },
                    { _id: '5', title: 'Product Manager', company: 'Linear', location: 'London', type: 'Full-time', salary: '110,000' },
                ];
                setJobs(mockJobs);
                // In a real app, you would use axios:
                // const res = await axios.get('/api/jobs');
                // setJobs(res.data);
            } catch (error) {
                console.error('Failed to load jobs:', error);
                setError('Could not load job listings. Please try again later.');
            }
        };

        fetchJobs();
    }, []);

    // Filtering logic
    const filteredJobs = jobs.filter(job =>
        job.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
        job.location.toLowerCase().includes(locationFilter.toLowerCase())
    );

    // Particle system for background
    const particles = Array.from({ length: 30 }, (_, i) => ({
        id: i,
        x: `${Math.random() * 100}%`,
        y: `${Math.random() * 100}%`,
        size: Math.random() * 2 + 1,
        duration: Math.random() * 15 + 10,
        delay: Math.random() * 3,
    }));

    return (
        <div className="relative min-h-screen bg-background text-foreground pt-24 px-4 sm:px-6 lg:px-8 overflow-hidden">
            {/* Background Particles */}
            <div className="absolute inset-0 z-0">
                {particles.map((p) => (
                    <motion.div
                        key={p.id}
                        className="absolute rounded-full bg-purple-500/20"
                        style={{ left: p.x, top: p.y, width: p.size, height: p.size }}
                        animate={{ y: [0, -80, 0], opacity: [0, 0.8, 0] }}
                        transition={{ duration: p.duration, delay: p.delay, repeat: Infinity, ease: "easeInOut" }}
                    />
                ))}
            </div>
            
            <Navbar />

            <div className="relative z-10 max-w-5xl mx-auto">
                {/* Header */}
                <motion.div
                    initial={{ opacity: 0, y: -30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.7, ease: "easeOut" }}
                    className="text-center mb-12"
                >
                    <h1 className="text-4xl md:text-5xl font-bold mb-3">
                        <span className="bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
                            Discover Your Next Role
                        </span>
                    </h1>
                    <p className="text-lg text-foreground/70">
                        Search through thousands of open positions worldwide.
                    </p>
                </motion.div>

                {/* Filter Section */}
                <motion.div
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.7, delay: 0.2, ease: "easeOut" }}
                    className="bg-card/50 backdrop-blur-lg border border-white/10 rounded-2xl p-6 mb-12 flex flex-col md:flex-row items-center gap-4 shadow-lg"
                >
                    <div className="relative w-full">
                        <Search className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-foreground/50" />
                        <input
                            type="text"
                            placeholder="Filter by title (e.g., Frontend)"
                            className="w-full pl-12 pr-4 py-3 bg-transparent border border-white/10 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>
                    <div className="relative w-full">
                        <MapPin className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-foreground/50" />
                        <input
                            type="text"
                            placeholder="Filter by location (e.g., Remote)"
                            className="w-full pl-12 pr-4 py-3 bg-transparent border border-white/10 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400"
                            value={locationFilter}
                            onChange={(e) => setLocationFilter(e.target.value)}
                        />
                    </div>
                </motion.div>

                {error && (
                    <div className="text-center text-red-400 mb-6 bg-red-500/10 p-4 rounded-lg">
                        {error}
                    </div>
                )}

                {/* Job Listings Grid */}
                <motion.div
                    variants={containerVariants}
                    initial="hidden"
                    animate="visible"
                    className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
                >
                    {filteredJobs.map((job) => (
                        <motion.div key={job._id} variants={itemVariants}>
                            <Link
                                to={`/job/${job._id}`}
                                className="block h-full bg-card/50 backdrop-blur-lg p-6 rounded-2xl border border-white/10 transition-all duration-300 hover:border-blue-400/50 hover:shadow-2xl hover:shadow-blue-500/10 hover:-translate-y-2 group"
                            >
                                <h3 className="text-xl font-bold text-foreground group-hover:text-blue-400 transition-colors">
                                    {job.title}
                                </h3>
                                <p className="text-foreground/80 mt-1">{job.company}</p>
                                <div className="flex flex-col sm:flex-row sm:items-center text-sm text-foreground/60 mt-4 gap-x-4 gap-y-2">
                                    <div className="flex items-center gap-1.5">
                                        <MapPin className="h-4 w-4" /> {job.location}
                                    </div>
                                    <div className="flex items-center gap-1.5">
                                        <Briefcase className="h-4 w-4" /> {job.type}
                                    </div>
                                </div>
                                <div className="border-t border-white/10 my-4"></div>
                                <div className="flex items-center justify-between">
                                    <div className="flex items-center gap-1.5 text-green-400 font-semibold">
                                        <DollarSign className="h-4 w-4" /> ${job.salary}
                                    </div>
                                    <span className="text-xs text-foreground/50 group-hover:text-blue-400 transition-colors">
                                        View Details &rarr;
                                    </span>
                                </div>
                            </Link>
                        </motion.div>
                    ))}
                </motion.div>
                
                {filteredJobs.length === 0 && !error && (
                    <motion.div 
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        className="text-center py-16 text-foreground/70"
                    >
                        <p>No jobs match your current filters.</p>
                    </motion.div>
                )}
            </div>
        </div>
    );
}
