//job-portal-frontend\src\pages\Dashboard\PostJob.jsx
import React, { useState } from "react";

function PostJobForm() {
  const [form, setForm] = useState({
    company: "",
    title: "",
    type: "full-time",
    location: "",
    salary: "",
    description: ""
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(false);

  // Animate form on component mount
  React.useEffect(() => {
    setTimeout(() => setIsFormVisible(true), 100);
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    const token = localStorage.getItem("token");

    const res = await fetch("http://localhost:5000/api/jobs/post", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(form),
    });

    const data = await res.json();
    if (res.ok) {
      alert("Job posted successfully!");
      setForm({ company: "", title: "", type: "Full-time", location: "", salary: "", description: "" });
    } else {
      alert(data.error || "Error posting job");
    }
    setIsSubmitting(false);
  };

  return (
    <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-100 font-sans">
      <h1 className="text-3xl font-bold text-white mb-8">Post a New Job</h1>
      
      <div
        className={`bg-gray-800 p-8 rounded-2xl shadow-xl transition-all duration-500 ease-in-out transform ${
          isFormVisible ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
        }`}
      >
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <label className="block">
              <span className="text-gray-400 text-sm">Company Name</span>
              <input name="company" value={form.company} onChange={handleChange} placeholder="Company Name" required className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200" />
            </label>
            <label className="block">
              <span className="text-gray-400 text-sm">Job Title</span>
              <input name="title" value={form.title} onChange={handleChange} placeholder="Job Title" required className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200" />
            </label>
            <label className="block">
              <span className="text-gray-400 text-sm">Job Type</span>
              <select
                  name="type"
                  value={form.type}
                  onChange={handleChange}
                  className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200"
                >
                  <option value="full-time">Full-time</option>
                  <option value="part-time">Part-time</option>
                  <option value="internship">Internship</option>
                  <option value="contract">Contract</option>
                  <option value="freelance">Freelance</option>
              </select>
            </label>
            <label className="block">
              <span className="text-gray-400 text-sm">Job Location</span>
              <input name="location" value={form.location} onChange={handleChange} placeholder="Job Location" required className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200" />
            </label>
            <label className="block">
              <span className="text-gray-400 text-sm">Salary (optional)</span>
              <input name="salary" value={form.salary} onChange={handleChange} placeholder="Salary (e.g., $50,000)" className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200" />
            </label>
          </div>
          
          <label className="block mb-6">
            <span className="text-gray-400 text-sm">Job Description</span>
            <textarea name="description" value={form.description} onChange={handleChange} placeholder="Job Description" required rows="5" className="mt-1 block w-full bg-gray-700 border border-gray-600 rounded-md py-2 px-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors duration-200" />
          </label>
          
          <button
            type="submit"
            disabled={isSubmitting}
            className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-purple-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isSubmitting ? "Posting Job..." : "Post Job"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default PostJobForm;
