import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Briefcase, MapPin } from 'lucide-react';
import axios from 'axios';

export default function BrowseJobs() {
  const [jobs, setJobs] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
  const fetchJobs = async () => {
    try {
      const res = await axios.get('/api/jobs');
      setJobs(res.data);
    } catch (error) {
      console.error('Failed to load jobs:', error);
    }
  };

  fetchJobs();
}, []);

  return (
    <div className="min-h-screen bg-blue-50 px-6 py-10">
      <h2 className="text-3xl font-bold text-blue-800 mb-8 text-center">Job Listings</h2>

      {error && (
        <div className="text-center text-red-600 mb-6">
          {error}
        </div>
      )}

      <div className="grid gap-6 max-w-4xl mx-auto">
        {jobs.map((job) => (
          <Link
            to={`/job/${job._id}`} // ✅ MongoDB uses _id
            key={job._id}
            className="bg-white p-6 rounded-lg shadow hover:shadow-md transition block"
          >
            <h3 className="text-xl font-semibold text-blue-700">{job.title}</h3>
            <p className="text-gray-600">{job.company}</p>
            <div className="flex items-center text-sm text-gray-500 mt-2">
              <MapPin className="h-4 w-4 mr-1" /> {job.location}
              <Briefcase className="h-4 w-4 ml-4 mr-1" /> {job.type}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
