import React, { useEffect, useState } from "react";
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { motion } from "framer-motion";
import { Users, Briefcase, CheckCircle, Mail } from 'lucide-react';
import { getEmployerStats, getApplicants } from "../../services/api";

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

const Overview = () => {
    const [employer, setEmployer] = useState({
        name: "",
        email: "",
        companyName: ""
    });
    const [stats, setStats] = useState({ totalJobs: 0, totalApplicants: 0 });
    const [applicants, setApplicants] = useState([]); // Store all applicants
    const [applicantsChartData, setApplicantsChartData] = useState({
        labels: [],
        datasets: [],
    });

    useEffect(() => {
        const storedEmployer = JSON.parse(localStorage.getItem("user"));
        if (storedEmployer) {
            setEmployer({
                name: storedEmployer.name || "No Name",
                email: storedEmployer.email || "No Email",
                companyName: storedEmployer.companyName || "No Company"
            });
        }

        const fetchDashboardData = async () => {
            try {
                const statsRes = await getEmployerStats();
                setStats(statsRes.data);

                const employerId = localStorage.getItem("employerId");
                if (employerId) {
                    const applicantsRes = await getApplicants(employerId);
                    const fetchedApplicants = applicantsRes?.data?.applicants || applicantsRes?.data || [];
                    setApplicants(fetchedApplicants); // Save all applicants
                    
                    const monthlyCounts = {};
                    fetchedApplicants.forEach(app => {
                        const month = new Date(app.appliedAt).toLocaleString('default', { month: 'short' });
                        monthlyCounts[month] = (monthlyCounts[month] || 0) + 1;
                    });

                    setApplicantsChartData({
                        labels: Object.keys(monthlyCounts),
                        datasets: [{
                            label: 'New Applicants',
                            data: Object.values(monthlyCounts),
                            backgroundColor: 'rgba(129, 140, 248, 0.6)',
                            borderColor: 'rgba(129, 140, 248, 1)',
                            borderWidth: 1,
                            borderRadius: 5,
                        }],
                    });
                }
            } catch (err) {
                console.error("Error fetching dashboard data:", err);
            }
        };
        fetchDashboardData();

    }, []);
    
    const chartOptions = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
            legend: {
                labels: { color: '#e0e0e0', font: { size: 14 } },
            },
            tooltip: {
                mode: 'index',
                intersect: false,
                backgroundColor: 'rgba(0, 0, 0, 0.8)',
                titleFont: { size: 14 },
                bodyFont: { size: 12 },
            }
        },
        scales: {
            x: {
                grid: { color: 'rgba(255, 255, 255, 0.1)' },
                ticks: { color: '#a0a0a0' },
            },
            y: {
                grid: { color: 'rgba(255, 255, 255, 0.1)' },
                ticks: { color: '#a0a0a0', beginAtZero: true },
            },
        },
    };

    const containerVariants = {
        hidden: { opacity: 0 },
        visible: { opacity: 1, transition: { staggerChildren: 0.15 } }
    };

    const itemVariants = {
        hidden: { opacity: 0, y: 20 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.5, ease: 'easeOut' } }
    };
    
    // Get the 3 most recent applicants
    const recentApplicants = applicants.slice(0, 3);

    return (
        <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            className="w-full"
        >
            <motion.h2 variants={itemVariants} className="text-3xl font-bold mb-8 bg-gradient-to-r from-purple-300 to-blue-300 bg-clip-text text-transparent">
                Employer Overview
            </motion.h2>

            {/* Top Stats */}
            <motion.div variants={itemVariants} className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                <StatCard icon={Briefcase} title="Total Jobs" value={stats.totalJobs} color="blue" />
                <StatCard icon={Users} title="Total Applicants" value={stats.totalApplicants} color="purple" />
                <StatCard icon={CheckCircle} title="Total Hires" value="0" color="cyan" />
            </motion.div>

            {/* Employer Info & Recent Applicants */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
                <motion.div variants={itemVariants} className="lg:col-span-1 bg-black/20 backdrop-blur-lg p-6 rounded-xl border border-white/10 flex flex-col items-center text-center">
                    <img
                        src="https://placehold.co/100x100/8b5cf6/ffffff?text=C"
                        alt="Company Logo"
                        className="w-24 h-24 rounded-full object-cover border-4 border-purple-500 mb-4"
                    />
                    <h3 className="text-2xl font-bold text-white">{employer.companyName}</h3>
                    <p className="text-purple-400 text-sm font-semibold">{employer.name}</p>
                    <p className="text-foreground/70 text-sm mt-1">{employer.email}</p>
                </motion.div>
                
                {/* --- REPLACED HIRING CHART WITH RECENT APPLICANTS --- */}
                <motion.div variants={itemVariants} className="lg:col-span-2 bg-black/20 backdrop-blur-lg p-6 rounded-xl border border-white/10">
                    <h3 className="text-xl font-semibold text-white mb-4">Recent Applicants</h3>
                    <div className="space-y-4">
                        {recentApplicants.length > 0 ? recentApplicants.map(app => (
                            <div key={app._id} className="p-3 bg-slate-800/50 rounded-lg border border-slate-700/50">
                                <p className="font-semibold text-white">{app.candidate?.name || "Unknown Applicant"}</p>
                                <p className="text-sm text-foreground/70 flex items-center gap-2 mt-1">
                                    <Mail size={14} /> {app.candidate?.email || "No email"}
                                </p>
                            </div>
                        )) : (
                            <p className="text-sm text-foreground/70 text-center py-8">No recent applications.</p>
                        )}
                    </div>
                </motion.div>
            </div>

            {/* Application Trends */}
            <motion.div variants={itemVariants} className="bg-black/20 backdrop-blur-lg p-6 rounded-xl border border-white/10">
                 <h3 className="text-xl font-semibold text-white mb-4">Application Trends</h3>
                 <div className="h-80">
                    <Bar data={applicantsChartData} options={chartOptions} />
                 </div>
            </motion.div>
        </motion.div>
    );
};

// Reusable Stat Card Component
const StatCard = ({ icon: Icon, title, value, color }) => {
    const colors = {
        blue: 'text-blue-400 bg-blue-500/20',
        purple: 'text-purple-400 bg-purple-500/20',
        cyan: 'text-cyan-400 bg-cyan-500/20',
    };

    return (
        <div className="bg-black/20 backdrop-blur-lg p-6 rounded-xl border border-white/10 flex items-center gap-4">
            <div className={`p-3 rounded-lg ${colors[color]}`}>
                <Icon className="h-6 w-6" />
            </div>
            <div>
                <p className="text-sm text-foreground/70">{title}</p>
                <p className="text-3xl font-bold">{value}</p>
            </div>
        </div>
    );
};

export default Overview;
