// src/pages/Auth/LoginEmployer.jsx
import React, { useState, useContext } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext"; 
import '../../styles/Auth.css';

const EmployerLogin = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/employers/login", formData);

      setMessage("Login successful");

       // ✅ Ensure user has role = employer
      const employerUser = {
        ...res.data.employer,
        role: "employer"
      };

      // ✅ Update AuthContext
      login(res.data.employer, res.data.token);

      // Save employerId for ApplicantsList
      localStorage.setItem("employerId", res.data.employer._id);  // ✅ Add this line
      localStorage.setItem("role", "employer");
      localStorage.setItem("user", JSON.stringify(res.data.employer || {}));



      // ✅ Redirect employer directly to dashboard
      navigate("/");
    } catch (err) {
      setMessage(err.response?.data?.message || "Error logging in");
    }
  };

  return (
    <div className="container">
      <div className="login-card-container">
        <div className="login-left-panel">
          <div className="logo">Your Logo</div>
          <h2 className="welcome-heading">Hello, Employer!</h2>
          <p className="welcome-text">
            Post jobs and find the best talent for your company. Join us today!
          </p>
          <button className="view-more-btn">View more</button>
        </div>

        <div className="login-right-panel">
          <h2 className="form-heading">Employer Login</h2>
          {message && <p className="message">{message}</p>}
          <form onSubmit={handleSubmit} className="login-form">
            <input
              type="email"
              name="email"
              placeholder="Email address"
              value={formData.email}
              onChange={handleChange}
              required
              className="form-input"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
              className="form-input"
            />
            <div className="form-extras">
              <label className="remember-me">
                <input type="checkbox" /> Remember me
              </label>
              <Link to="/forgot-password-employer" className="forgot-password-link">
                Forgot password?
              </Link>
            </div>
            <div className="form-actions">
              <button type="submit" className="login-btn">Login</button>
              <Link to="/signup-employer" className="signup-btn">Sign up</Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EmployerLogin;
