//job-portal-frontend\src\pages\Dashboard\Candidate\Dashboards.js
import React, { useState } from "react";
import Overview from "./Overview";   // âœ… same folder
import Profile from "./Profile";     // âœ… same folder
import "../../../styles/EmployerDashboard.css"; // âœ… go 3 levels up

const CandidateDashboard = () => {
  const [activeTab, setActiveTab] = useState("overview");

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2 className="sidebar-title">Candidate</h2>
        <ul className="sidebar-menu">
          <li
            className={activeTab === "overview" ? "active" : ""}
            onClick={() => setActiveTab("overview")}
          >
            ðŸ“Š Overview
          </li>
          <li
            className={activeTab === "profile" ? "active" : ""}
            onClick={() => setActiveTab("profile")}
          >
            ðŸ‘¤ Profile
          </li>
        </ul>
      </div>

      {/* Content */}
      <div className="dashboard-content">
        {activeTab === "overview" && <Overview />}
        {activeTab === "profile" && <Profile />}
      </div>
    </div>
  );
};

export default CandidateDashboard;
