import React, { useState } from "react";
import Joblist from "../../pages/Dashboard/Joblist";
import Overview from "../../pages/Dashboard/Overview";
import PostJobForm from "../../pages/Dashboard/PostJob";
import ApplicantsList from "../../pages/Dashboard/Applicants";
import CompanyProfile from "../../pages/Dashboard/CompanyProfile";
import Stats from "../../pages/Dashboard/Stats";
import '../../styles/EmployerDashboard.css';

const EmployerDashboard = () => {
  const [activeTab, setActiveTab] = useState("stats");

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2 className="sidebar-title">Employer</h2>
        <ul className="sidebar-menu">
          <li
           classname={activeTab=="overview" ? "active" : ""}
           onClick={() => setActiveTab("overview")}
          >
            Overview
          </li>
          <li
            className={activeTab === "stats" ? "active" : ""}
            onClick={() => setActiveTab("stats")}
          >
            üìä Stats
          </li>
          <li
            className={activeTab === "jobs" ? "active" : ""}
            onClick={() => setActiveTab("jobs")}
          >
            üíº Job Management
          </li>
          <li
            className={activeTab === "applicants" ? "active" : ""}
            onClick={() => setActiveTab("applicants")}
          >
            üë• Applicants
          </li>
          <li
            className={activeTab === "profile" ? "active" : ""}
            onClick={() => setActiveTab("profile")}
          >
            üè¢ Company Profile
          </li>
          <li
            className={activeTab === "post" ? "active" : ""}
            onClick={() => setActiveTab("post")}
          >
            ‚ûï Post a Job
          </li>
        </ul>
      </div>

      {/* Content */}
      <div className="dashboard-content">
        {activeTab === "overview" && <Overview />}
        {activeTab === "stats" && <Stats />}
        {activeTab === "jobs" && <Joblist />}
        {activeTab === "applicants" && <ApplicantsList />}
        {activeTab === "profile" && <CompanyProfile />}
        {activeTab === "post" && <PostJobForm />}
      </div>
    </div>
  );
};

export default EmployerDashboard;
