import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { getApplicants } from "../../services/api";
import { User, Mail, FileText, Briefcase } from 'lucide-react';

// Animated Background
const AnimatedBackground = () => {
    return (
        <div className="absolute inset-0 z-0 overflow-hidden">
            <div className="absolute inset-0 h-full w-full bg-background bg-[linear-gradient(to_right,#8080800a_1px,transparent_1px),linear-gradient(to_bottom,#8080800a_1px,transparent_1px)] bg-[size:24px_36px]"></div>
            <motion.div
                className="absolute inset-0 bg-[radial-gradient(ellipse_at_top_left,rgba(147,51,234,0.15),transparent_50%)]"
                animate={{ y: [20, 0, 20] }}
                transition={{ duration: 18, repeat: Infinity, ease: "easeInOut" }}
            />
        </div>
    );
};

// Main ApplicantsList Component
const ApplicantsList = () => {
    // --- YOUR ORIGINAL LOGIC - UNCHANGED ---
    const [applicants, setApplicants] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchApplicants = async () => {
            try {
                const employerId = localStorage.getItem("employerId");
                if (!employerId) {
                    console.error("No employerId in localStorage.");
                    setLoading(false);
                    return;
                }
                const response = await getApplicants(employerId);
                const data = response?.data?.applicants || response?.data || [];
                setApplicants(data);
            } catch (err) {
                console.error("Error fetching applicants:", err);
            } finally {
                setLoading(false);
            }
        };
        fetchApplicants();
    }, []);
    // --- END OF YOUR LOGIC ---

    const containerVariants = {
        hidden: { opacity: 0 },
        visible: { opacity: 1, transition: { staggerChildren: 0.1 } }
    };

    const itemVariants = {
        hidden: { opacity: 0, y: 20 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.5, ease: 'easeOut' } }
    };

    if (loading) {
        return <div className="text-center p-20 text-foreground/70">Loading applicants...</div>;
    }

    return (
        <div className="relative w-full min-h-full">
            <AnimatedBackground />
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="relative z-10"
            >
                <h1 className="text-3xl font-bold mb-8 bg-gradient-to-r from-purple-300 to-blue-300 bg-clip-text text-transparent">
                    All Applicants
                </h1>

                {!applicants.length ? (
                    <div className="text-center p-20 text-foreground/70 bg-black/20 rounded-lg">
                        <p>You have no applicants yet.</p>
                    </div>
                ) : (
                    <motion.div
                        variants={containerVariants}
                        initial="hidden"
                        animate="visible"
                        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
                    >
                        {applicants.map((applicant) => (
                            <motion.div
                                key={applicant._id}
                                variants={itemVariants}
                                className="bg-black/20 backdrop-blur-lg p-6 rounded-2xl border border-white/10 flex flex-col transition-transform duration-300 hover:-translate-y-2"
                            >
                                <div className="flex items-center gap-4 mb-4">
                                    <div className="w-12 h-12 bg-purple-500/20 rounded-full flex items-center justify-center border border-purple-500/30">
                                        <User className="h-6 w-6 text-purple-400" />
                                    </div>
                                    <div>
                                        <h3 className="font-semibold text-white truncate">{applicant.candidate?.name || "Unknown"}</h3>
                                        <p className="text-sm text-gray-400 flex items-center gap-1.5"><Mail size={12}/> {applicant.candidate?.email || "N/A"}</p>
                                    </div>
                                </div>

                                <div className="text-sm text-gray-300 space-y-2 mb-4 flex-grow">
                                    <p className="flex items-start gap-2">
                                        <Briefcase size={14} className="mt-1 text-purple-400 flex-shrink-0"/>
                                        <span>Applied for: <span className="font-semibold text-purple-300">{applicant.job?.title || "No job title"}</span></span>
                                    </p>
                                </div>

                                <div className="mt-auto pt-4 border-t border-gray-700 flex justify-between items-center">
                                    <span
                                        className={`inline-block px-3 py-1 text-xs font-bold rounded-full capitalize ${
                                            applicant.status === "hired" ? "bg-green-600 text-white"
                                            : applicant.status === "rejected" ? "bg-red-600 text-white"
                                            : "bg-blue-600 text-white"
                                        }`}
                                    >
                                        {applicant.status}
                                    </span>
                                    {applicant.candidate?.resume && (
                                        <a
                                            href={applicant.candidate.resume}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className="text-sm text-purple-400 hover:underline flex items-center gap-1.5"
                                        >
                                            <FileText size={14} /> View Resume
                                        </a>
                                    )}
                                </div>
                            </motion.div>
                        ))}
                    </motion.div>
                )}
            </motion.div>
        </div>
    );
};

export default ApplicantsList;
