//job-portal-frontend\src\pages\Dashboard\Candidate\Dashboards.js
import React from 'react';
import { motion } from 'framer-motion';
import { Outlet, Link, useLocation } from 'react-router-dom';
import { User, LayoutDashboard } from 'lucide-react';
import Navbar from '../../../components/Navbar'; // Assuming a global Navbar

// Animated Background Component
const AnimatedBackground = () => {
    return (
        <div
            className="absolute inset-0 z-0 bg-cover bg-center"
            style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1454165804606-c3d57bc86b40?q=80&w=2070&auto=format&fit=crop)' }}
        >
            <div className="absolute inset-0 bg-background/90 backdrop-blur-sm" />
        </div>
    );
};

// Sidebar Component
const Sidebar = () => {
    const location = useLocation();
    const navItems = [
        { path: '/candidate-dashboard/overview', label: 'Overview', icon: LayoutDashboard },
        { path: '/candidate-dashboard/profile', label: 'Profile', icon: User },
    ];

    return (
        <aside className="w-64 flex-shrink-0 bg-black/20 backdrop-blur-lg border-r border-white/10 p-6">
            <h2 className="text-xl font-bold mb-8 text-white">Dashboard</h2>
            <nav className="space-y-2">
                {navItems.map(item => (
                    <Link
                        key={item.label}
                        to={item.path}
                        className={`flex items-center gap-3 px-4 py-3 rounded-lg transition-colors ${
                            location.pathname === item.path
                                ? 'bg-blue-500 text-white'
                                : 'text-foreground/70 hover:bg-white/10 hover:text-white'
                        }`}
                    >
                        <item.icon className="h-5 w-5" />
                        <span className="font-medium">{item.label}</span>
                    </Link>
                ))}
            </nav>
        </aside>
    );
};

// Main Dashboard Component
const CandidateDashboard = () => {
    return (
        <>
            <Navbar />
            <div className="relative min-h-screen w-full flex pt-16">
                <AnimatedBackground />
                <Sidebar />
                <main className="relative flex-1 p-4 md:p-8 overflow-y-auto">
                    {/* This Outlet will render either the Overview or Profile component based on the URL */}
                    <Outlet /> 
                </main>
            </div>
        </>
    );
};

export default CandidateDashboard;
