import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Briefcase, MapPin, Search, DollarSign } from 'lucide-react';
import axios from 'axios';
import { motion } from 'framer-motion';
import Navbar from '../components/Navbar';

// Re-usable animation variants
const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
};

const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
        opacity: 1,
        y: 0,
        transition: {
            duration: 0.5,
            ease: "easeOut"
        }
    }
};

export default function BrowseJobs() {
    // --- YOUR ORIGINAL LOGIC - UNCHANGED ---
    const [jobs, setJobs] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const res = await axios.get('/api/jobs'); // Using your original API endpoint
                setJobs(res.data);
            } catch (error) {
                console.error('Failed to load jobs:', error);
                setError('Could not load job listings. Please try again later.'); // Added a user-friendly error
            }
        };

        fetchJobs();
    }, []);
    // --- END OF YOUR ORIGINAL LOGIC ---

    // --- CURSOR EFFECT LOGIC ---
    const [mousePosition, setMousePosition] = useState({ x: -100, y: -100 });
    useEffect(() => {
        const handleMouseMove = (e) => {
            setMousePosition({ x: e.clientX, y: e.clientY });
        };
        window.addEventListener('mousemove', handleMouseMove);
        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    const cursorVariants = {
        default: {
            x: mousePosition.x - 16, // Center the cursor
            y: mousePosition.y - 16,
            transition: { type: "spring", stiffness: 500, damping: 30 }
        }
    };
    // --- END CURSOR EFFECT LOGIC ---

    // Enhanced particle system for more depth and visibility
    const particles = Array.from({ length: 50 }, (_, i) => ({ // Increased particle count
        id: i,
        x: `${Math.random() * 100}%`,
        y: `${Math.random() * 100}%`,
        size: Math.random() * 3 + 1,
        duration: Math.random() * 20 + 15,
    }));

    return (
        <>
            {/* Custom Cursor Element */}
            <motion.div
                variants={cursorVariants}
                animate="default"
                className="fixed top-0 left-0 h-8 w-8 bg-white/10 rounded-full pointer-events-none z-[9999] backdrop-filter backdrop-blur-sm border border-white/20"
            />

            <div className="relative min-h-screen bg-background text-foreground pt-24 px-4 sm:px-6 lg:px-8 overflow-hidden cursor-none">
                {/* Background Particles & Grid */}
                <div className="absolute inset-0 z-0">
                    <div className="absolute inset-0 bg-[radial-gradient(ellipse_80%_80%_at_50%_-20%,rgba(120,119,198,0.3),rgba(255,255,255,0))]"></div>
                    {particles.map((p) => (
                        <motion.div
                            key={p.id}
                            className="absolute rounded-full bg-purple-500" // Removed the blur effect
                            style={{ left: p.x, top: p.y, width: p.size, height: p.size }}
                            animate={{ y: [0, -120, 0], opacity: [0, 0.8, 0.8, 0] }} // Increased max opacity
                            transition={{ duration: p.duration, repeat: Infinity, ease: "easeInOut" }} // Removed delay
                        />
                    ))}
                </div>
                
                <Navbar />

                <div className="relative z-10 max-w-5xl mx-auto">
                    {/* Header */}
                    <motion.div
                        initial={{ opacity: 0, y: -30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.7, ease: "easeOut" }}
                        className="text-center mb-12"
                    >
                        <h1 className="text-4xl md:text-5xl font-bold mb-3">
                            <span className="bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
                                Job Listings
                            </span>
                        </h1>
                        <p className="text-lg text-foreground/70">
                            Discover your next career opportunity.
                        </p>
                    </motion.div>

                    {error && (
                        <div className="text-center text-red-400 mb-6 bg-red-500/10 p-4 rounded-lg">
                            {error}
                        </div>
                    )}

                    {/* Job Listings Grid */}
                    <motion.div
                        variants={containerVariants}
                        initial="hidden"
                        animate="visible"
                        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
                    >
                        {jobs.map((job) => (
                            <motion.div key={job._id} variants={itemVariants}>
                                <Link
                                    to={`/job/${job._id}`}
                                    className="block h-full bg-card/50 backdrop-blur-lg p-6 rounded-2xl border border-white/10 transition-all duration-300 hover:border-blue-400/50 hover:shadow-2xl hover:shadow-blue-500/10 hover:-translate-y-2 group"
                                >
                                    <h3 className="text-xl font-bold text-foreground group-hover:text-blue-400 transition-colors">
                                        {job.title}
                                    </h3>
                                    <p className="text-foreground/80 mt-1">{job.company}</p>
                                    <div className="flex items-center text-sm text-foreground/60 mt-4 gap-x-4 gap-y-2">
                                        <div className="flex items-center gap-1.5">
                                            <MapPin className="h-4 w-4" /> {job.location}
                                        </div>
                                        <div className="flex items-center gap-1.5">
                                            <Briefcase className="h-4 w-4" /> {job.type}
                                        </div>
                                    </div>
                                    <div className="border-t border-white/10 my-4"></div>
                                    <span className="text-xs text-foreground/50 group-hover:text-blue-400 transition-colors">
                                        View Details &rarr;
                                    </span>
                                </Link>
                            </motion.div>
                        ))}
                    </motion.div>
                    
                    {jobs.length === 0 && !error && (
                        <motion.div 
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            className="text-center py-16 text-foreground/70"
                        >
                            <p>Loading jobs or no listings found.</p>
                        </motion.div>
                    )}
                </div>
            </div>
        </>
    );
}
