import { useEffect, useState } from 'react';
import { Link, useSearchParams } from 'react-router-dom'; // Import useSearchParams
import { Briefcase, MapPin, Search, DollarSign } from 'lucide-react';
import axios from 'axios';
import { motion } from 'framer-motion';
import Navbar from '../components/Navbar';

// Re-usable animation variants
const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
};

const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
        opacity: 1,
        y: 0,
        transition: {
            duration: 0.5,
            ease: "easeOut"
        }
    }
};

export default function BrowseJobs() {
    const [jobs, setJobs] = useState([]);
    const [error, setError] = useState('');
    const [searchParams] = useSearchParams(); // Get URL search parameters

    // Get search terms from the URL
    const keyword = searchParams.get('keyword') || '';
    const location = searchParams.get('location') || '';

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const res = await axios.get('/api/jobs');
                setJobs(res.data);
            } catch (error) {
                console.error('Failed to load jobs:', error);
                setError('Could not load job listings.');
            }
        };
        fetchJobs();
    }, []);

    // THIS IS THE NEW FILTERING LOGIC
    const filteredJobs = jobs.filter(job => {
        const titleMatch = job.title.toLowerCase().includes(keyword.toLowerCase());
        const companyMatch = job.company.toLowerCase().includes(keyword.toLowerCase());
        const locationMatch = job.location.toLowerCase().includes(location.toLowerCase());
        
        return (titleMatch || companyMatch) && locationMatch;
    });
    
    // ... (rest of your BrowseJobs.jsx component code, particles, etc.)
    // The JSX now uses `filteredJobs` instead of `jobs` to display the results.
    return (
        <>
            <Navbar />
            <div className="relative min-h-screen bg-background text-foreground pt-24 px-4 sm:px-6 lg:px-8 overflow-hidden">
                <div className="relative z-10 max-w-5xl mx-auto">
                    <motion.div 
                        initial={{ opacity: 0, y: -20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5 }}
                        className="text-center mb-12"
                    >
                        <h1 className="text-4xl md:text-5xl font-bold mb-3">
                            <span className="bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
                                Job Listings
                            </span>
                        </h1>
                        <p className="text-lg text-foreground/70">
                           Showing results for: <span className="font-semibold text-white">{keyword || "All Jobs"}</span> in <span className="font-semibold text-white">{location || "All Locations"}</span>
                        </p>
                    </motion.div>

                    {error && (
                        <div className="text-center text-red-400 mb-6 bg-red-500/10 p-4 rounded-lg">
                            {error}
                        </div>
                    )}

                    <motion.div 
                        variants={containerVariants}
                        initial="hidden"
                        animate="visible"
                        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
                    >
                        {filteredJobs.map((job) => (
                            <motion.div key={job._id} variants={itemVariants}>
                                <Link
                                    to={`/job/${job._id}`}
                                    className="block h-full bg-card/50 backdrop-blur-lg p-6 rounded-2xl border border-white/10 transition-all duration-300 hover:border-blue-400/50 hover:shadow-2xl hover:shadow-blue-500/10 hover:-translate-y-2 group"
                                >
                                    <h3 className="text-xl font-bold text-foreground group-hover:text-blue-400 transition-colors">
                                        {job.title}
                                    </h3>
                                    <p className="text-foreground/80 mt-1">{job.company}</p>
                                    <div className="flex items-center text-sm text-foreground/60 mt-4 gap-x-4 gap-y-2">
                                        <div className="flex items-center gap-1.5">
                                            <MapPin className="h-4 w-4" /> {job.location}
                                        </div>
                                        <div className="flex items-center gap-1.5">
                                            <Briefcase className="h-4 w-4" /> {job.type}
                                        </div>
                                    </div>
                                    <div className="border-t border-white/10 my-4"></div>
                                    <span className="text-xs text-foreground/50 group-hover:text-blue-400 transition-colors">
                                        View Details &rarr;
                                    </span>
                                </Link>
                            </motion.div>
                        ))}
                    </motion.div>
                    
                    {filteredJobs.length === 0 && !error && (
                        <motion.div 
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            className="text-center py-16 text-foreground/70"
                        >
                            <p>No jobs match your search criteria.</p>
                        </motion.div>
                    )}
                </div>
            </div>
        </>
    );
}
