import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Input } from '../components/ui/input';
import { Button } from '../components/ui/button';
import Navbar from "../components/Navbar";
import {
    Briefcase, MapPin, Search, DollarSign, Heart, Star, Zap,
    Target, ShieldCheck, Rocket, Clock, Building
} from 'lucide-react';
import { motion } from "framer-motion";

// --- DATA FOR THE PAGE ---

const featuredJobs = [
    {
        id: 1,
        title: 'Full Stack Developer',
        company: 'OpenAI',
        type: 'Full-time',
        location: 'Remote',
        salary: '$100,000',
        postedDate: '2 days ago',
        description: 'Work on cutting-edge AI tools and infrastructure. Join our team building the future of artificial intelligence and help create tools that empower millions of users worldwide.'
    },
    {
        id: 2,
        title: 'Python Developer',
        company: 'Auraix',
        type: 'Full-time',
        location: 'Mumbai',
        salary: '$60,000',
        postedDate: '1 day ago',
        description: 'Coding, designing, deploying, and debugging software projects, typically focusing on server-side (back-end) development. Work with cutting-edge Python frameworks and technologies.'
    },
    {
        id: 3,
        title: 'Web Developer',
        company: 'Auraix',
        type: 'Full-time',
        location: 'Mumbai',
        salary: '$55,000',
        postedDate: '3 days ago',
        description: 'Building, maintaining, and optimizing websites and web applications by writing code that translates design concepts into functional digital products.'
    }
];

const features = [
    {
        icon: Target,
        title: "AI-Powered Matching",
        description: "Our smart algorithm connects you with jobs that perfectly match your skills and career goals.",
        color: "blue"
    },
    {
        icon: ShieldCheck,
        title: "Verified Opportunities",
        description: "Every job posting is verified by our team, ensuring you apply to legitimate and high-quality roles.",
        color: "purple"
    },
    {
        icon: Rocket,
        title: "Fast-Track Applications",
        description: "Apply to jobs with a single click and get your profile directly in front of hiring managers.",
        color: "cyan"
    }
];

// --- ANIMATION VARIANTS ---

const sectionVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
        opacity: 1,
        y: 0,
        transition: {
            duration: 0.8,
            ease: "easeOut",
            staggerChildren: 0.2
        }
    }
};

const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } }
};


// --- HOME COMPONENT ---

export default function Home() {
    const [jobKeyword, setJobKeyword] = useState('');
    const [jobLocation, setJobLocation] = useState('');
    const navigate = useNavigate();

    const handleSearch = () => {
        navigate(`/browse-jobs?keyword=${jobKeyword}&location=${jobLocation}`);
    };

    const getCompanyGradient = (company) => {
        switch (company) {
            case "OpenAI": return "from-emerald-400 via-teal-400 to-cyan-400";
            case "Auraix": return "from-purple-400 via-violet-400 to-indigo-400";
            default: return "from-blue-400 via-purple-400 to-cyan-400";
        }
    };

    const getCompanyAccent = (company) => {
        switch (company) {
            case "OpenAI": return "text-emerald-400";
            case "Auraix": return "text-purple-400";
            default: return "text-blue-400";
        }
    };

    const particles = Array.from({ length: 50 }, (_, i) => ({
        id: i,
        x: `${Math.random() * 100}%`,
        y: `${Math.random() * 100}%`,
        size: Math.random() * 2 + 1,
        duration: Math.random() * 20 + 15,
        delay: Math.random() * 5,
    }));

    const popularSearches = ["Software Engineer", "Product Manager", "Designer", "Marketing", "Sales"];

    return (
        <>
            <Navbar />

            {/* Hero Section */}
            <section className="relative w-full min-h-screen flex items-center justify-center py-24 px-4 sm:px-6 lg:px-8 overflow-hidden">
                {/* Background Effects */}
                <div className="absolute inset-0 z-0">
                    {particles.map((p) => (
                        <motion.div
                            key={p.id}
                            className="absolute rounded-full bg-blue-500/30"
                            style={{ left: p.x, top: p.y, width: p.size, height: p.size }}
                            animate={{ y: [0, -100, 0], opacity: [0, 1, 0], scale: [0.5, 1, 0.5] }}
                            transition={{ duration: p.duration, delay: p.delay, repeat: Infinity, ease: "easeInOut" }}
                        />
                    ))}
                    <motion.div
                        className="absolute top-0 left-0 w-96 h-96 bg-purple-600/30 rounded-full filter blur-3xl"
                        animate={{ x: [0, 100, 0], y: [0, 50, 0], scale: [1, 1.2, 1] }}
                        transition={{ duration: 20, repeat: Infinity, ease: "easeInOut" }}
                    />
                    <motion.div
                        className="absolute bottom-0 right-0 w-96 h-96 bg-cyan-600/30 rounded-full filter blur-3xl"
                        animate={{ x: [0, -100, 0], y: [0, -50, 0], scale: [1, 1.2, 1] }}
                        transition={{ duration: 25, repeat: Infinity, ease: "easeInOut", delay: 5 }}
                    />
                </div>

                <motion.div
                    variants={sectionVariants}
                    initial="hidden"
                    animate="visible"
                    className="relative z-10 text-center max-w-5xl mx-auto"
                >
                    <motion.div variants={itemVariants} className="inline-flex items-center gap-3 bg-card/50 backdrop-blur-lg border border-white/10 rounded-full px-6 py-3 mb-8">
                        <Zap className="h-5 w-5 text-blue-400" />
                        <span className="font-medium text-sm bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
                            ✨ Over 10,000+ Premium Opportunities
                        </span>
                    </motion.div>

                    <motion.h1 variants={itemVariants} className="text-5xl md:text-6xl lg:text-7xl font-bold mb-6 leading-tight text-foreground">
                        Find Your Dream Job. <br />
                        <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent">
                            You Deserve It.
                        </span>
                    </motion.h1>

                    <motion.p variants={itemVariants} className="text-lg md:text-xl text-foreground/70 mb-12 max-w-3xl mx-auto">
                        Explore thousands of opportunities from top companies and kickstart your career journey with confidence.
                    </motion.p>

                    <motion.div variants={itemVariants} className="relative max-w-4xl mx-auto">
                        <motion.div
                            className="absolute -inset-2 bg-gradient-to-r from-blue-500 via-purple-500 to-cyan-500 rounded-2xl blur-xl opacity-20"
                            animate={{ opacity: [0.2, 0.4, 0.2], scale: [1, 1.05, 1] }}
                            transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
                        />
                        <div className="relative bg-card/50 backdrop-blur-xl rounded-2xl shadow-2xl shadow-blue-500/10 p-6 border border-white/10">
                            <div className="flex flex-col md:flex-row items-center gap-4">
                                <div className="relative w-full">
                                    <Briefcase className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-blue-400 pointer-events-none" />
                                    <Input
                                        type="text"
                                        placeholder="Job title, keywords, or company"
                                        value={jobKeyword}
                                        onChange={(e) => setJobKeyword(e.target.value)}
                                        onKeyPress={(e) => { if (e.key === 'Enter') handleSearch(); }}
                                        className="text-gray-900" // Black text color
                                    />
                                </div>
                                <div className="relative w-full">
                                    <MapPin className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-purple-400 pointer-events-none" />
                                    <Input
                                        type="text"
                                        placeholder="Location (e.g., New York, Remote)"
                                        value={jobLocation}
                                        onChange={(e) => setJobLocation(e.target.value)}
                                        onKeyPress={(e) => { if (e.key === 'Enter') handleSearch(); }}
                                        className="text-gray-900" // Black text color
                                    />
                                </div>
                                <Button onClick={handleSearch} className="w-full md:w-auto flex-shrink-0">
                                    <Search className="h-5 w-5 mr-2" />
                                    Find Jobs
                                </Button>
                            </div>
                            <div className="mt-6 text-center">
                                <span className="text-foreground/60 text-sm mr-4">🔥 Trending:</span>
                                <div className="inline-flex flex-wrap justify-center gap-2">
                                    {popularSearches.map((term) => (
                                        <button key={term} className="px-4 py-1.5 bg-card/60 text-foreground/80 rounded-full hover:bg-card/80 hover:text-foreground transition-all duration-300 border border-white/10 text-sm">
                                            {term}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        </div>
                    </motion.div>
                </motion.div>
            </section>

            {/* Featured Jobs Section */}
            <section className="py-24 px-4 sm:px-6 lg:px-8">
                <motion.div
                    variants={sectionVariants}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, amount: 0.2 }}
                    className="text-center max-w-4xl mx-auto mb-16"
                >
                    <motion.h2 variants={itemVariants} className="text-4xl md:text-5xl font-bold mb-4">
                        Handpicked Opportunities
                    </motion.h2>
                    <motion.p variants={itemVariants} className="text-lg text-foreground/70">
                        Discover roles at innovative companies that match your skills and passion.
                    </motion.p>
                </motion.div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-7xl mx-auto">
                    {featuredJobs.map((job, index) => (
                        <motion.div
                            key={job.id}
                            initial={{ opacity: 0, y: 50, scale: 0.9 }}
                            whileInView={{ opacity: 1, y: 0, scale: 1 }}
                            viewport={{ once: true }}
                            transition={{ duration: 0.6, delay: index * 0.1, ease: "easeOut" }}
                            whileHover={{ y: -10, transition: { duration: 0.3 } }}
                            className="bg-card/50 backdrop-blur-lg rounded-2xl border border-white/10 p-6 flex flex-col h-full"
                        >
                            <div className="flex items-start justify-between mb-4">
                                <div className="flex items-center gap-4">
                                    <div className={`w-14 h-14 bg-gradient-to-br ${getCompanyGradient(job.company)} rounded-xl flex items-center justify-center text-white text-2xl font-bold shadow-lg`}>
                                        {job.company.charAt(0)}
                                    </div>
                                    <div>
                                        <h3 className="font-bold text-lg text-foreground">{job.title}</h3>
                                        <p className={`font-medium text-sm ${getCompanyAccent(job.company)}`}>{job.company}</p>
                                    </div>
                                </div>
                                <button className="p-2 rounded-lg border border-white/10 hover:bg-red-500/10 transition-colors">
                                    <Heart className="h-5 w-5 text-foreground/60" />
                                </button>
                            </div>
                            <div className="flex flex-wrap items-center gap-x-4 gap-y-2 text-sm text-foreground/70 mb-4">
                                <div className="flex items-center gap-1.5"><MapPin className="h-4 w-4" /> {job.location}</div>
                                <div className="flex items-center gap-1.5"><Clock className="h-4 w-4" /> {job.postedDate}</div>
                                <div className="flex items-center gap-1.5"><DollarSign className="h-4 w-4 text-green-400" /> <span className="text-green-400 font-semibold">{job.salary}</span></div>
                            </div>
                            <p className="text-foreground/70 text-sm leading-relaxed mb-6 flex-grow">
                                {job.description}
                            </p>
                            <div className="flex items-center justify-between">
                                <span className="bg-blue-500/10 text-blue-400 px-3 py-1 rounded-full text-xs font-medium border border-blue-400/20">{job.type}</span>
                                <Button size="sm">
                                    <Zap className="h-4 w-4 mr-2" />
                                    Apply Now
                                </Button>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </section>

            {/* "Why Choose Us?" Section */}
            <section className="py-24 px-4 sm:px-6 lg:px-8">
                <motion.div
                    variants={sectionVariants}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, amount: 0.2 }}
                    className="text-center max-w-4xl mx-auto mb-16"
                >
                    <motion.h2 variants={itemVariants} className="text-4xl md:text-5xl font-bold mb-4">
                        Why JobHunt?
                    </motion.h2>
                    <motion.p variants={itemVariants} className="text-lg text-foreground/70">
                        We provide the tools and opportunities to accelerate your career.
                    </motion.p>
                </motion.div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-7xl mx-auto">
                    {features.map((feature, index) => (
                        <motion.div
                            key={index}
                            initial={{ opacity: 0, y: 50 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            viewport={{ once: true }}
                            transition={{ duration: 0.6, delay: index * 0.15, ease: "easeOut" }}
                            className="bg-card/50 backdrop-blur-lg rounded-2xl p-8 text-center border border-white/10"
                        >
                            <div className={`inline-flex p-4 rounded-lg bg-gradient-to-br from-${feature.color}-500/20 to-${feature.color}-500/10 mb-6`}>
                                <feature.icon className={`h-8 w-8 text-${feature.color}-300`} />
                            </div>
                            <h3 className="text-xl font-bold mb-3">{feature.title}</h3>
                            <p className="text-foreground/70">{feature.description}</p>
                        </motion.div>
                    ))}
                </div>
            </section>

            {/* Footer */}
            <footer className="border-t border-white/10 py-8 text-center">
                <p className="text-foreground/70">&copy; 2025 JobHunt. All rights reserved.</p>
            </footer>
        </>
    );
}
