import React, { useEffect, useState } from "react";
import { Bar, Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Overview = () => {
  const [employer, setEmployer] = useState({
    name: "",
    email: "",
    companyName: ""
  });
  const [isStatsVisible, setIsStatsVisible] = useState(false);
  const [isEmployerVisible, setIsEmployerVisible] = useState(false);
  const [isJobStatsVisible, setIsJobStatsVisible] = useState(false);
  const [isGraphsVisible, setIsGraphsVisible] = useState(false);

  useEffect(() => {
    // Fetch employer details from localStorage
    const storedEmployer = JSON.parse(localStorage.getItem("user"));
    if (storedEmployer) {
      setEmployer({
        name: storedEmployer.name || "No Name",
        email: storedEmployer.email || "No Email",
        companyName: storedEmployer.companyName || "No Company"
      });
    }

    // Staggered animations
    const statsTimer = setTimeout(() => setIsStatsVisible(true), 200);
    const employerTimer = setTimeout(() => setIsEmployerVisible(true), 400);
    const jobStatsTimer = setTimeout(() => setIsJobStatsVisible(true), 600);
    const graphsTimer = setTimeout(() => setIsGraphsVisible(true), 800);

    return () => {
      clearTimeout(statsTimer);
      clearTimeout(employerTimer);
      clearTimeout(jobStatsTimer);
      clearTimeout(graphsTimer);
    };
  }, []);

  const applicantsData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'New Applicants',
        data: [120, 190, 150, 210, 250, 200],
        backgroundColor: '#6b46c1',
        borderColor: '#6b46c1',
        borderWidth: 1,
      },
    ],
  };

  const hiringTrendsData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Candidates Hired',
        data: [20, 35, 30, 45, 50, 40],
        borderColor: '#38b2ac',
        backgroundColor: 'rgba(56, 178, 172, 0.2)',
        tension: 0.4,
        fill: true,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        labels: {
          color: 'white',
        },
      },
      title: {
        display: false,
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        callbacks: {
          label: function(context) {
            let label = context.dataset.label || '';
            if (label) {
              label += ': ';
            }
            if (context.parsed.y !== null) {
              label += context.parsed.y;
            }
            return label;
          }
        }
      }
    },
    scales: {
      x: {
        grid: {
          color: '#383838',
        },
        ticks: {
          color: '#e0e0e0',
        },
      },
      y: {
        grid: {
          color: '#383838',
        },
        ticks: {
          color: '#e0e0e0',
          beginAtZero: true,
        },
      },
    },
  };

  return (
    <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-100 font-sans">
      {/* Top Stats */}
      <div
        className={`grid grid-cols-1 md:grid-cols-3 gap-6 mb-8 transition-all duration-500 ease-in-out transform ${
          isStatsVisible ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
        }`}
      >
        <div className="bg-gray-800 p-6 rounded-2xl shadow-lg transition-transform duration-300 hover:-translate-y-2 transform hover:shadow-2xl">
          <h4 className="text-sm font-medium text-gray-400">Total Jobs</h4>
          <p className="text-4xl font-bold mt-2 text-white">350</p>
        </div>
        <div className="bg-gray-800 p-6 rounded-2xl shadow-lg transition-transform duration-300 hover:-translate-y-2 transform hover:shadow-2xl">
          <h4 className="text-sm font-medium text-gray-400">Total Applicants</h4>
          <p className="text-4xl font-bold mt-2 text-white">1245</p>
        </div>
        <div className="bg-gray-800 p-6 rounded-2xl shadow-lg transition-transform duration-300 hover:-translate-y-2 transform hover:shadow-2xl">
          <h4 className="text-sm font-medium text-gray-400">Total Hiring</h4>
          <p className="text-4xl font-bold mt-2 text-white">200</p>
        </div>
      </div>

      <hr className="border-gray-700 my-8" />

      {/* Middle Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        {/* Employer Overview */}
        <div
          className={`bg-gray-800 p-6 rounded-2xl shadow-lg transition-all duration-500 ease-in-out transform ${
            isEmployerVisible ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
          }`}
        >
          <h4 className="text-lg font-semibold text-white mb-4">Employer Overview</h4>
          <div className="flex items-center space-x-4">
            <img
              src="https://imgs.search.brave.com/y0RdP_M5ctsg5I9kG1v6cVdA8PaJh35mBB1G8ceHn-0/rs:fit:860:0:0:0/g:ce/aHR0cHM6Ly90NC5m/dGNkbi5uZXQvanBn/LzAzLzMyLzU5LzY1/LzM2MF9GXzMzMjU5/NjUzNV9sQWRMaGY2/S3piVzZQV1hCV2VJ/RlRvdlRpaTFkcmti/VC5qcGc"
              alt="Employer"
              className="w-20 h-20 rounded-full object-cover border-4 border-purple-500"
            />
            <div>
              <h5 className="text-2xl font-bold text-white">{employer.name}</h5>
              <p className="text-purple-400 text-sm font-semibold">Employer</p>
              <p className="text-gray-400 text-sm mt-1">{employer.email}</p>
              <p className="text-gray-400 text-sm">{employer.companyName}</p>
            </div>
          </div>
        </div>

        {/* Job Statistics */}
        <div
          className={`bg-gray-800 p-6 rounded-2xl shadow-lg flex flex-col items-center transition-all duration-500 ease-in-out transform ${
            isJobStatsVisible ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
          }`}
        >
          <h4 className="text-lg font-semibold text-white mb-4">Job Statistics</h4>
          <div className="flex items-center justify-between w-full">
            <div className="relative w-28 h-28">
              <svg className="w-full h-full transform -rotate-90">
                <circle
                  className="text-gray-700"
                  strokeWidth="10"
                  stroke="currentColor"
                  fill="transparent"
                  r="45"
                  cx="56"
                  cy="56"
                />
                <circle
                  className="text-purple-500"
                  strokeWidth="10"
                  strokeDasharray={2 * Math.PI * 45}
                  strokeDashoffset={(2 * Math.PI * 45) * (1 - 0.7)}
                  strokeLinecap="round"
                  stroke="currentColor"
                  fill="transparent"
                  r="45"
                  cx="56"
                  cy="56"
                  style={{ transition: 'stroke-dashoffset 0.8s ease-in-out' }}
                />
              </svg>
              <span className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 text-2xl font-bold text-white">70%</span>
            </div>
            <div className="space-y-2">
              <p className="text-sm text-gray-300">
                <span className="text-green-400 mr-2">✅</span>New job posted
              </p>
              <p className="text-sm text-gray-300">
                <span className="text-blue-400 mr-2">📩</span>Application received
              </p>
              <p className="text-sm text-gray-300">
                <span className="text-purple-400 mr-2">🎉</span>Candidate hired
              </p>
            </div>
          </div>
        </div>
      </div>
      
      {/* Graphs Section */}
      <div
        className={`grid grid-cols-1 md:grid-cols-2 gap-6 transition-all duration-500 ease-in-out transform ${
          isGraphsVisible ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
        }`}
      >
        <div className="bg-gray-800 p-6 rounded-2xl shadow-lg">
          <h4 className="text-lg font-semibold text-white mb-4">Application Trends</h4>
          <Bar data={applicantsData} options={options} />
        </div>
        <div className="bg-gray-800 p-6 rounded-2xl shadow-lg">
          <h4 className="text-lg font-semibold text-white mb-4">Hiring Trends</h4>
          <Line data={hiringTrendsData} options={options} />
        </div>
      </div>
    </div>
  );
};

export default Overview;
