import { useEffect, useState } from "react";
import axios from "axios";
import { getMyJobs } from "../../services/api";

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isListVisible, setIsListVisible] = useState(false);
  const [applicants, setApplicants] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);

  useEffect(() => {
    const run = async () => {
      try {
        const { data } = await getMyJobs();
        setJobs(data);
      } catch (e) {
        console.error("Error loading jobs:", e);
      } finally {
        setLoading(false);
        setTimeout(() => setIsListVisible(true), 100);
      }
    };
    run();
  }, []);

  const handleViewApplicants = async (jobId) => {
    try {
      const { data } = await axios.get(`http://localhost:5000/api/applications/${jobId}/applicants`);
      setApplicants(data);
      setSelectedJob(jobId);
    } catch (err) {
      console.error("Error fetching applicants:", err);
    }
  };

  if (loading) {
    return (
      <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-400 font-sans flex items-center justify-center">
        <p>Loading jobs...</p>
      </div>
    );
  }

  return (
    <div className="flex-1 p-6 md:p-10 bg-gray-950 min-h-screen text-gray-100 font-sans">
      <h1 className="text-3xl font-bold text-white mb-8">My Job Listings</h1>

      {!jobs.length ? (
        <div className="text-center p-20 text-gray-400">
          <p>You haven't posted any jobs yet.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {jobs.map((job, index) => (
            <div
              key={job._id}
              className="bg-gray-800 p-6 rounded-2xl shadow-lg"
            >
              <h3 className="text-xl font-semibold text-white truncate">{job.title}</h3>
              <p className="text-sm text-gray-400 mt-1">{job.company} - {job.location}</p>
              <p className="text-sm text-gray-400">{job.type}</p>
              <p className="text-gray-300 mt-4 text-sm line-clamp-3">{job.description}</p>

              <div className="mt-4 pt-4 border-t border-gray-700">
                <button
                  onClick={() => handleViewApplicants(job._id)}
                  className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded-lg transition-colors duration-200 text-sm"
                >
                  View Applicants
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Applicants Modal */}
      {selectedJob && (
        <div className="mt-10 bg-gray-900 p-6 rounded-xl">
          <h2 className="text-2xl font-bold mb-4">Applicants</h2>
          {!applicants.length ? (
            <p className="text-gray-400">No applicants yet.</p>
          ) : (
            <ul className="space-y-4">
              {applicants.map((app, idx) => (
                <li key={idx} className="bg-gray-800 p-4 rounded-lg">
                  <p className="text-white font-medium">{app.name} ({app.email})</p>
                  <p className="text-gray-400 mt-1">{app.coverLetter}</p>
                  {app.resume && (
                    <a
                      href={`http://localhost:5000/${app.resume}`} 
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-purple-400 underline mt-2 inline-block"
                    >
                      View Resume
                    </a>
                  )}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default JobList;
