// src/pages/Auth/LoginCandidate.jsx
// src/pages/Auth/LoginCandidate.jsx
import React, { useState, useContext, useEffect } from "react";
import API from "../../services/api";
import { useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { motion, AnimatePresence } from "motion/react";
import { User, Lock, Mail, Eye, EyeOff, Briefcase, Target, Award, TrendingUp, CheckCircle, ArrowRight } from "lucide-react";

// Different floating particles system for login page
const LoginFloatingParticles = () => {
    const particles = Array.from({ length: 35 }, (_, i) => ({
        id: i,
        x: Math.random() * 100,
        y: Math.random() * 100,
        size: Math.random() * 2 + 1,
        duration: Math.random() * 20 + 15,
        delay: Math.random() * 5,
        opacity: Math.random() * 0.5 + 0.1,
    }));

    return (
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
            {particles.map((particle) => (
                <motion.div
                    key={particle.id}
                    className="absolute rounded-full"
                    style={{
                        left: `${particle.x}%`,
                        top: `${particle.y}%`,
                        width: `${particle.size}px`,
                        height: `${particle.size}px`,
                        background: `linear-gradient(135deg, 
                            rgba(59, 130, 246, ${particle.opacity}), 
                            rgba(147, 51, 234, ${particle.opacity}))`,
                    }}
                    animate={{
                        y: [-30, -120, -30],
                        x: [-20, 20, -20],
                        scale: [1, 1.5, 1],
                        opacity: [particle.opacity, particle.opacity * 2, particle.opacity],
                    }}
                    transition={{
                        duration: particle.duration,
                        delay: particle.delay,
                        repeat: Infinity,
                        ease: "easeInOut",
                    }}
                />
            ))}
        </div>
    );
};

// Career-focused background animation
const CareerBackground = () => {
    return (
        <div className="absolute inset-0 z-0 overflow-hidden">
            {/* Diagonal gradient lines */}
            <motion.div
                className="absolute top-0 left-0 w-full h-full"
                style={{
                    background: `linear-gradient(45deg, 
                        transparent 48%, 
                        rgba(59, 130, 246, 0.05) 49%, 
                        rgba(59, 130, 246, 0.05) 51%, 
                        transparent 52%)`
                }}
                animate={{
                    backgroundPosition: ["0% 0%", "100% 100%"],
                }}
                transition={{
                    duration: 20,
                    repeat: Infinity,
                    ease: "linear",
                }}
            />
            
            {/* Floating career icons */}
            <motion.div
                className="absolute top-1/4 left-1/6 w-12 h-12 opacity-10"
                animate={{
                    y: [-15, 15, -15],
                    rotate: [0, 180, 360],
                }}
                transition={{
                    duration: 15,
                    repeat: Infinity,
                    ease: "easeInOut",
                }}
            >
                <Target className="w-full h-full text-blue-400" />
            </motion.div>
            
            <motion.div
                className="absolute top-2/3 right-1/5 w-10 h-10 opacity-15"
                animate={{
                    y: [15, -15, 15],
                    rotate: [360, 180, 0],
                }}
                transition={{
                    duration: 12,
                    repeat: Infinity,
                    ease: "easeInOut",
                }}
            >
                <Award className="w-full h-full text-purple-400" />
            </motion.div>
            
            <motion.div
                className="absolute bottom-1/4 left-1/3 w-8 h-8 opacity-20"
                animate={{
                    x: [-10, 10, -10],
                    y: [-5, 5, -5],
                    scale: [1, 1.2, 1],
                }}
                transition={{
                    duration: 8,
                    repeat: Infinity,
                    ease: "easeInOut",
                }}
            >
                <TrendingUp className="w-full h-full text-cyan-400" />
            </motion.div>

            {/* Animated gradient orbs */}
            <motion.div
                className="absolute top-1/5 right-1/4 w-40 h-40 bg-gradient-to-r from-blue-500/5 to-purple-500/5 rounded-full filter blur-3xl"
                animate={{
                    scale: [1, 1.2, 1],
                    opacity: [0.3, 0.5, 0.3],
                }}
                transition={{
                    duration: 10,
                    repeat: Infinity,
                    ease: "easeInOut",
                }}
            />
            
            <motion.div
                className="absolute bottom-1/5 left-1/4 w-32 h-32 bg-gradient-to-r from-purple-500/5 to-cyan-500/5 rounded-full filter blur-3xl"
                animate={{
                    scale: [1.2, 1, 1.2],
                    opacity: [0.5, 0.3, 0.5],
                }}
                transition={{
                    duration: 8,
                    repeat: Infinity,
                    ease: "easeInOut",
                }}
            />
        </div>
    );
};

// Enhanced input component
const AnimatedInput = ({ icon: Icon, type, name, placeholder, value, onChange, required }) => {
    const [isFocused, setIsFocused] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    return (
        <motion.div 
            className="relative group"
            whileFocus={{ scale: 1.02 }}
            transition={{ duration: 0.2 }}
        >
            <div className="relative">
                <div className="absolute left-3 top-1/2 -translate-y-1/2 z-10">
                    <Icon className={`h-5 w-5 transition-colors duration-300 ${isFocused ? 'text-blue-400' : 'text-foreground/50'}`} />
                </div>
                <input
                    type={type === 'password' && showPassword ? 'text' : type}
                    name={name}
                    placeholder={placeholder}
                    value={value}
                    onChange={onChange}
                    required={required}
                    onFocus={() => setIsFocused(true)}
                    onBlur={() => setIsFocused(false)}
                    className="w-full pl-12 pr-12 py-4 bg-background/50 border border-white/20 rounded-xl focus:ring-2 focus:ring-blue-400 focus:border-transparent transition-all duration-300 backdrop-blur-sm hover:border-white/30 placeholder:text-foreground/40"
                />
                {type === 'password' && (
                    <button
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        className="absolute right-3 top-1/2 -translate-y-1/2 text-foreground/50 hover:text-foreground/70 transition-colors"
                    >
                        {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
                    </button>
                )}
            </div>
            
            {/* Input glow effect */}
            <motion.div
                className="absolute inset-0 rounded-xl bg-gradient-to-r from-blue-500/20 to-purple-500/20 opacity-0 -z-10 blur-xl"
                animate={{ opacity: isFocused ? 1 : 0 }}
                transition={{ duration: 0.3 }}
            />
        </motion.div>
    );
};

const LoginCandidate = () => {
    // --- YOUR ORIGINAL LOGIC - PRESERVED EXACTLY ---
    const [formData, setFormData] = useState({ email: "", password: "" });
    const { login } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleChange = (e) =>
        setFormData({ ...formData, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await API.post("/auth/login", formData);

            // ✅ Update AuthContext
            login(res.data.user, res.data.token);

            // ✅ Candidate goes to home, then navbar shows Candidate Dashboard
            navigate("/");
        } catch (err) {
            console.error("Login failed:", err);
            alert("Invalid credentials. Please try again.");
        }
    };
    // --- END OF YOUR ORIGINAL LOGIC ---

    // Enhanced UI logic
    const [mousePosition, setMousePosition] = useState({ x: -100, y: -100 });
    const [isLoading, setIsLoading] = useState(false);
    
    useEffect(() => {
        const handleMouseMove = (e) => setMousePosition({ x: e.clientX, y: e.clientY });
        window.addEventListener('mousemove', handleMouseMove);
        return () => window.removeEventListener('mousemove', handleMouseMove);
    }, []);

    const containerVariants = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: { 
                staggerChildren: 0.1, 
                delayChildren: 0.2,
                duration: 0.8
            }
        }
    };

    const leftPanelVariants = {
        hidden: { opacity: 0, x: -50 },
        visible: { 
            opacity: 1, 
            x: 0,
            transition: { 
                duration: 0.8, 
                ease: [0.16, 1, 0.3, 1] 
            } 
        }
    };

    const rightPanelVariants = {
        hidden: { opacity: 0, x: 50 },
        visible: { 
            opacity: 1, 
            x: 0,
            transition: { 
                duration: 0.8, 
                ease: [0.16, 1, 0.3, 1] 
            } 
        }
    };

    const itemVariants = {
        hidden: { opacity: 0, y: 20 },
        visible: { 
            opacity: 1, 
            y: 0,
            transition: { 
                duration: 0.6, 
                ease: [0.16, 1, 0.3, 1] 
            } 
        }
    };

    return (
        <>
            {/* Enhanced Custom Cursor */}
            <motion.div
                style={{ x: mousePosition.x - 16, y: mousePosition.y - 16 }}
                className="fixed top-0 left-0 h-8 w-8 bg-gradient-to-r from-blue-400/20 to-purple-400/20 rounded-full pointer-events-none z-[9999] backdrop-filter backdrop-blur-sm border border-white/30"
                transition={{ type: "spring", stiffness: 500, damping: 30 }}
            />
            
            <div className="min-h-screen bg-background text-foreground relative overflow-hidden cursor-none">
                {/* Background Effects */}
                <CareerBackground />
                <LoginFloatingParticles />

                {/* Main Container */}
                <motion.div
                    variants={containerVariants}
                    initial="hidden"
                    animate="visible"
                    className="relative z-10 min-h-screen flex"
                >
                    {/* Left Panel - Welcome Section */}
                    <motion.div
                        variants={leftPanelVariants}
                        className="hidden lg:flex lg:w-1/2 relative"
                    >
                        <div className="w-full flex flex-col justify-center px-12 xl:px-16 relative">
                            {/* Background gradient for left panel */}
                            <div className="absolute inset-0 bg-gradient-to-br from-blue-500/10 via-purple-500/5 to-transparent" />
                            
                            <motion.div variants={itemVariants} className="relative z-10">
                                {/* Logo */}
                                <motion.div 
                                    className="flex items-center gap-3 mb-12"
                                    whileHover={{ scale: 1.05 }}
                                    transition={{ duration: 0.3 }}
                                >
                                    <div className="p-3 bg-gradient-to-r from-blue-500/20 to-purple-500/20 rounded-xl backdrop-blur-sm border border-white/20">
                                        <Briefcase className="h-8 w-8 text-blue-400" />
                                    </div>
                                    <span className="text-2xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
                                        Your Logo
                                    </span>
                                </motion.div>

                                {/* Welcome Message */}
                                <motion.h2 
                                    variants={itemVariants}
                                    className="text-4xl xl:text-5xl font-bold mb-6 leading-tight"
                                >
                                    <span className="block text-foreground">Hello,</span>
                                    <span className="block bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent">
                                        welcome!
                                    </span>
                                </motion.h2>

                                <motion.p 
                                    variants={itemVariants}
                                    className="text-lg text-foreground/70 mb-8 leading-relaxed max-w-md"
                                >
                                    Find your dream job with us. Start your career journey today and discover opportunities that match your skills and aspirations.
                                </motion.p>

                                {/* Feature highlights */}
                                <motion.div variants={itemVariants} className="space-y-4 mb-8">
                                    {[
                                        { icon: CheckCircle, text: "Access to premium job listings" },
                                        { icon: Target, text: "Personalized job recommendations" },
                                        { icon: TrendingUp, text: "Career growth opportunities" }
                                    ].map((feature, index) => (
                                        <motion.div
                                            key={index}
                                            className="flex items-center gap-3 text-foreground/60"
                                            initial={{ opacity: 0, x: -20 }}
                                            animate={{ opacity: 1, x: 0 }}
                                            transition={{ delay: index * 0.1 + 1 }}
                                        >
                                            <feature.icon className="h-5 w-5 text-green-400" />
                                            <span>{feature.text}</span>
                                        </motion.div>
                                    ))}
                                </motion.div>

                                <motion.button
                                    variants={itemVariants}
                                    className="group bg-gradient-to-r from-blue-500/20 to-purple-500/20 text-blue-400 font-semibold py-3 px-6 rounded-xl border border-blue-400/30 hover:border-blue-400/50 transition-all duration-300 backdrop-blur-sm"
                                    whileHover={{ scale: 1.05, x: 5 }}
                                    whileTap={{ scale: 0.95 }}
                                >
                                    <span className="flex items-center gap-2">
                                        Learn more
                                        <ArrowRight className="h-4 w-4 group-hover:translate-x-1 transition-transform" />
                                    </span>
                                </motion.button>
                            </motion.div>
                        </div>
                    </motion.div>

                    {/* Right Panel - Login Form */}
                    <motion.div
                        variants={rightPanelVariants}
                        className="w-full lg:w-1/2 flex items-center justify-center p-6 lg:p-12"
                    >
                        <div className="w-full max-w-md">
                            {/* Mobile logo for small screens */}
                            <motion.div 
                                variants={itemVariants}
                                className="lg:hidden flex items-center justify-center gap-3 mb-8"
                            >
                                <div className="p-3 bg-gradient-to-r from-blue-500/20 to-purple-500/20 rounded-xl backdrop-blur-sm border border-white/20">
                                    <Briefcase className="h-6 w-6 text-blue-400" />
                                </div>
                                <span className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
                                    Your Logo
                                </span>
                            </motion.div>

                            {/* Form Container */}
                            <motion.div
                                className="relative group"
                                whileHover={{ scale: 1.01 }}
                                transition={{ duration: 0.3 }}
                            >
                                {/* Card glow effect */}
                                <div className="absolute -inset-1 bg-gradient-to-r from-blue-600/20 via-purple-600/20 to-cyan-600/20 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500 blur-xl" />
                                
                                <div className="relative bg-card/60 backdrop-blur-xl rounded-2xl shadow-2xl shadow-purple-500/10 border border-white/20 p-8">
                                    <motion.h2 
                                        variants={itemVariants}
                                        className="text-2xl font-bold text-center mb-8 bg-gradient-to-r from-foreground via-blue-100 to-purple-100 bg-clip-text text-transparent"
                                    >
                                        Candidate Login
                                    </motion.h2>

                                    {/* PRESERVED YOUR ORIGINAL FORM WITH ENHANCED STYLING */}
                                    <form onSubmit={handleSubmit} className="space-y-6">
                                        <motion.div variants={itemVariants}>
                                            <AnimatedInput
                                                icon={Mail}
                                                type="email"
                                                name="email"
                                                placeholder="Email address"
                                                value={formData.email}
                                                onChange={handleChange}
                                                required
                                            />
                                        </motion.div>

                                        <motion.div variants={itemVariants}>
                                            <AnimatedInput
                                                icon={Lock}
                                                type="password"
                                                name="password"
                                                placeholder="Password"
                                                value={formData.password}
                                                onChange={handleChange}
                                                required
                                            />
                                        </motion.div>

                                        <motion.div variants={itemVariants} className="flex items-center justify-between text-sm">
                                            <label className="flex items-center gap-2 text-foreground/70 cursor-pointer group">
                                                <input 
                                                    type="checkbox" 
                                                    className="rounded border-white/20 bg-transparent focus:ring-blue-400 focus:ring-offset-0 text-blue-500"
                                                />
                                                <span className="group-hover:text-foreground transition-colors">Remember me</span>
                                            </label>
                                            <Link 
                                                to="/forgot-password" 
                                                className="text-blue-400 hover:text-blue-300 transition-colors hover:underline"
                                            >
                                                Forgot password?
                                            </Link>
                                        </motion.div>

                                        <motion.div variants={itemVariants} className="space-y-4">
                                            <motion.button
                                                type="submit"
                                                disabled={isLoading}
                                                className="w-full group relative overflow-hidden bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white font-semibold py-4 rounded-xl shadow-lg transition-all duration-300 disabled:opacity-50"
                                                whileHover={{ 
                                                    scale: 1.02,
                                                    boxShadow: "0 20px 40px rgba(59, 130, 246, 0.3)"
                                                }}
                                                whileTap={{ scale: 0.98 }}
                                            >
                                                {/* Button shine effect */}
                                                <motion.div
                                                    className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-transparent via-white/20 to-transparent -skew-x-12"
                                                    initial={{ x: "-100%" }}
                                                    whileHover={{ x: "100%" }}
                                                    transition={{ duration: 0.6 }}
                                                />
                                                <span className="relative flex items-center justify-center gap-2">
                                                    {isLoading ? (
                                                        <motion.div
                                                            animate={{ rotate: 360 }}
                                                            transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                                                            className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full"
                                                        />
                                                    ) : (
                                                        <>
                                                            <User className="h-5 w-5" />
                                                            Login
                                                        </>
                                                    )}
                                                </span>
                                            </motion.button>

                                            <motion.div
                                                whileHover={{ scale: 1.02 }}
                                                whileTap={{ scale: 0.98 }}
                                            >
                                                <Link 
                                                    to="/signup-candidate" 
                                                    className="w-full flex items-center justify-center gap-2 bg-transparent text-blue-400 font-semibold py-4 rounded-xl border-2 border-blue-400/30 hover:border-blue-400/50 hover:bg-blue-400/5 transition-all duration-300"
                                                >
                                                    <User className="h-5 w-5" />
                                                    Sign up
                                                </Link>
                                            </motion.div>
                                        </motion.div>
                                    </form>
                                </div>
                            </motion.div>
                        </div>
                    </motion.div>
                </motion.div>
            </div>
        </>
    );
};

export default LoginCandidate;
