// src/pages/JobDetail.js
import { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import { Briefcase, MapPin, DollarSign, Building, CheckCircle, XCircle, ArrowLeft } from 'lucide-react';
import Navbar from '../components/Navbar';

// New component for the spiral background effect
const SpiralBackground = () => {
    return (
        <div className="absolute inset-0 z-0 overflow-hidden">
            <motion.div
                className="absolute top-1/2 left-1/2"
                animate={{ rotate: 360 }}
                transition={{ duration: 50, repeat: Infinity, ease: 'linear' }}
            >
                <div className="absolute h-[100vh] w-[100vh] rounded-[50%] border-[2px] border-t-cyan-500 border-l-purple-500 border-b-blue-500 border-r-transparent filter blur-2xl opacity-50" />
                <div className="absolute h-[80vh] w-[80vh] rounded-[50%] border-[2px] border-t-blue-500 border-l-cyan-500 border-b-purple-500 border-r-transparent filter blur-2xl opacity-40" style={{ animationDelay: '-2s' }} />
                <div className="absolute h-[60vh] w-[60vh] rounded-[50%] border-[2px] border-t-purple-500 border-l-blue-500 border-b-cyan-500 border-r-transparent filter blur-xl opacity-30" style={{ animationDelay: '-4s' }} />
            </motion.div>
        </div>
    );
};


export default function JobDetails() {
    // --- YOUR ORIGINAL LOGIC - RESTORED ---
    const { id } = useParams();
    const [job, setJob] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        coverLetter: '',
        resume: null,
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchJob = async () => {
            try {
                // RESTORED your original fetch call
                const res = await fetch(`/api/jobs/${id}`);
                if (!res.ok) {
                    throw new Error(`HTTP error! status: ${res.status}`);
                }
                const data = await res.json();
                setJob(data);
            } catch (err) {
                console.error("Failed to fetch job", err);
                setError("Failed to load job details. Please try again later.");
            } finally {
                setLoading(false);
            }
        };
        fetchJob();
    }, [id]);

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        if (name === 'resume') {
            setFormData({ ...formData, resume: files[0] });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = new FormData();
        data.append('jobId', job._id);
        data.append('name', formData.name);
        data.append('email', formData.email);
        data.append('coverLetter', formData.coverLetter);
        data.append('resume', formData.resume);

        try {
            await axios.post('http://localhost:5000/api/apply', data, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            alert('Your application has been submitted successfully!');
            setShowForm(false);
            setFormData({ name: '', email: '', coverLetter: '', resume: null });
        } catch (error) {
            console.error('Error submitting application:', error);
            alert('There was an error submitting your application. Please try again.');
        }
    };
    // --- END OF YOUR LOGIC ---

    // --- UI ENHANCEMENT LOGIC ---
    const [mousePosition, setMousePosition] = useState({ x: -100, y: -100 });
    useEffect(() => {
        const handleMouseMove = (e) => setMousePosition({ x: e.clientX, y: e.clientY });
        window.addEventListener('mousemove', handleMouseMove);
        return () => window.removeEventListener('mousemove', handleMouseMove);
    }, []);
    // --- END UI LOGIC ---

    const containerVariants = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: { staggerChildren: 0.1, delayChildren: 0.3 }
        }
    };

    const itemVariants = {
        hidden: { opacity: 0, y: 20 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.5, ease: 'easeOut' } }
    };

    if (loading) {
        return (
            <div className="flex justify-center items-center min-h-screen bg-background text-foreground">
                <div className="text-xl font-semibold animate-pulse">Loading job details...</div>
            </div>
        );
    }

    if (error || !job) {
        return (
            <div className="flex flex-col justify-center items-center min-h-screen bg-background text-foreground p-4">
                <XCircle className="h-16 w-16 text-red-500 mb-4" />
                <h2 className="text-2xl font-bold mb-2">Oops! Something went wrong.</h2>
                <p className="text-foreground/70">{error || "This job could not be found."}</p>
                <Link to="/browse-jobs" className="mt-6 bg-blue-500 text-white font-semibold py-2 px-5 rounded-lg hover:bg-blue-600 transition-colors">
                    Back to Listings
                </Link>
            </div>
        );
    }

    return (
        <>
            <motion.div
                style={{ x: mousePosition.x - 16, y: mousePosition.y - 16 }}
                className="fixed top-0 left-0 h-8 w-8 bg-white/10 rounded-full pointer-events-none z-[9999] backdrop-filter backdrop-blur-sm border border-white/20"
                transition={{ type: "spring", stiffness: 500, damping: 30 }}
            />
            <Navbar />
            <div className="relative min-h-screen bg-background text-foreground p-4 md:p-8 pt-24 flex items-center justify-center overflow-hidden cursor-none">
                {/* Background Effects */}
                <SpiralBackground />

                {/* Main Content Card */}
                <motion.div
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.7, ease: [0.16, 1, 0.3, 1] }}
                    className="relative z-10 max-w-4xl w-full"
                >
                    <motion.div
                        variants={containerVariants}
                        initial="hidden"
                        animate="visible"
                        className="bg-card/50 backdrop-blur-xl rounded-2xl shadow-2xl shadow-purple-500/10 border border-white/10"
                    >
                        <div className="p-8 md:p-10">
                            <motion.div variants={itemVariants}>
                                <Link to="/browse-jobs" className="flex items-center gap-2 text-sm text-blue-400 hover:text-blue-300 transition-colors mb-6">
                                    <ArrowLeft className="h-4 w-4" />
                                    Back to all jobs
                                </Link>
                            </motion.div>
                            
                            <motion.h1 variants={itemVariants} className="text-3xl md:text-4xl font-bold text-foreground mb-3">{job.title}</motion.h1>
                            
                            <motion.div variants={itemVariants} className="flex flex-wrap items-center gap-x-6 gap-y-2 text-foreground/80 mb-6">
                                <span className="flex items-center gap-2"><Building className="h-4 w-4 text-purple-400" /> {job.company}</span>
                                <span className="flex items-center gap-2"><MapPin className="h-4 w-4 text-cyan-400" /> {job.location}</span>
                                <span className="flex items-center gap-2"><Briefcase className="h-4 w-4 text-amber-400" /> {job.type}</span>
                                <span className="flex items-center gap-2"><DollarSign className="h-4 w-4 text-green-400" /> {job.salary}</span>
                            </motion.div>

                            <motion.div variants={itemVariants} className="prose prose-invert max-w-none text-foreground/80 mb-8">
                                <h3 className="text-xl font-semibold text-foreground mb-2">Job Description</h3>
                                <p className="whitespace-pre-line">{job.description || "No description provided."}</p>
                            </motion.div>

                            {job.requirements?.length > 0 && (
                                <motion.div variants={itemVariants} className="mb-8">
                                    <h3 className="text-xl font-semibold text-foreground mb-3">Key Requirements</h3>
                                    <ul className="space-y-2">
                                        {job.requirements.map((req, index) => (
                                            <li key={index} className="flex items-start gap-3">
                                                <CheckCircle className="h-5 w-5 text-green-400 mt-0.5 flex-shrink-0" />
                                                <span className="text-foreground/80">{req}</span>
                                            </li>
                                        ))}
                                    </ul>
                                </motion.div>
                            )}

                            <motion.div variants={itemVariants} className="border-t border-white/10 pt-6">
                                <motion.button
                                    onClick={() => setShowForm(!showForm)}
                                    className="w-full md:w-auto bg-gradient-to-r from-blue-500 to-purple-600 text-white font-bold py-3 px-8 rounded-lg shadow-lg hover:shadow-purple-500/30 transform transition-all duration-300 hover:scale-105"
                                    whileHover={{ scale: 1.05 }}
                                    whileTap={{ scale: 0.95 }}
                                >
                                    {showForm ? 'Cancel Application' : 'Apply for this Job'}
                                </motion.button>

                                <AnimatePresence>
                                    {showForm && (
                                        <motion.form
                                            onSubmit={handleSubmit}
                                            initial={{ opacity: 0, height: 0, marginTop: 0 }}
                                            animate={{ opacity: 1, height: 'auto', marginTop: '2rem' }}
                                            exit={{ opacity: 0, height: 0, marginTop: 0 }}
                                            transition={{ duration: 0.5, ease: [0.16, 1, 0.3, 1] }}
                                            className="overflow-hidden"
                                        >
                                            <div className="p-6 bg-card/70 border border-white/10 rounded-lg">
                                                <h3 className="text-2xl font-bold text-foreground mb-5">Application Form</h3>
                                                <div className="space-y-4">
                                                    <input type="text" name="name" placeholder="Your Full Name" required onChange={handleChange} value={formData.name} className="w-full p-3 bg-transparent border border-white/20 rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none" />
                                                    <input type="email" name="email" placeholder="Your Email Address" required onChange={handleChange} value={formData.email} className="w-full p-3 bg-transparent border border-white/20 rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none" />
                                                    <textarea name="coverLetter" placeholder="Cover Letter (Optional)" onChange={handleChange} value={formData.coverLetter} rows="4" className="w-full p-3 bg-transparent border border-white/20 rounded-lg focus:ring-2 focus:ring-blue-400 focus:outline-none" />
                                                    <div>
                                                        <label htmlFor="resume" className="block text-sm font-medium text-foreground/70 mb-2">Upload Resume</label>
                                                        <input type="file" name="resume" id="resume" required onChange={handleChange} className="w-full text-foreground/80 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-500/10 file:text-blue-300 hover:file:bg-blue-500/20 cursor-pointer" />
                                                    </div>
                                                    <button type="submit" className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 rounded-lg transition-colors">Submit Application</button>
                                                </div>
                                            </div>
                                        </motion.form>
                                    )}
                                </AnimatePresence>
                            </motion.div>
                        </div>
                    </motion.div>
                </motion.div>
            </div>
        </>
    );
}
