// src/components/Navbar.jsx
import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { user, isLoggedIn, logout } = useContext(AuthContext);

  return (
    <nav className="bg-white shadow-sm border-b w-full px-6 py-4 fixed top-0 left-0 z-50">
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        {/* Logo */}
        <div className="text-xl font-bold text-blue-600">JobHunt</div>

        {/* Nav Links */}
        <div className="space-x-6 text-sm font-medium">
          <Link to="/" className="hover:text-blue-600">Home</Link>
          <Link to="/browse-jobs" className="hover:text-blue-600">Browse Jobs</Link>
          <Link to="/post-job" className="hover:text-blue-600">Post a Job</Link>

          {isLoggedIn ? (
            <>
              {/* ✅ Show dashboard based on role */}
              {user?.role === "candidate" && (
                <Link to="/candidate-dashboard" className="hover:text-blue-600">
                  Candidate Dashboard
                </Link>
              )}
              {user?.role === "employer" && (
                <Link to="/employer-dashboard" className="hover:text-blue-600">
                  Employer Dashboard
                </Link>
              )}

              {/* ✅ Show profile name */}
              <span className="font-medium text-gray-600">
                {user?.name || "Profile"}
              </span>

              {/* ✅ Logout button */}
              <button
                onClick={logout}
                className="hover:text-red-600 ml-4"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              {/* ✅ Only show single Login/Signup button */}
              <Link
                to="/Auth"
                className="bg-white text-blue-700 py-2.5 px-6 rounded-full shadow-lg hover:bg-gray-100 transition-all duration-300 transform hover:scale-105 font-semibold"
              >
                Login / Sign Up
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
